//-- Fade panel when not in use
var kb1ShortcutFired = false;
var sfrpSideBar    = $('#gmSFRPSideBar');
sfrpSideBar.hover (
    function () {
        $(this).stop (true, false).fadeTo (50,  1  );
        kb1ShortcutFired = false;
    },
    function () {
        if ( ! kb1ShortcutFired ) {
            $(this).stop (true, false).fadeTo (900, 0.1);
        }
        kb1ShortcutFired = false;
    }
);
sfrpSideBar.fadeTo (2900, 0.1);

//-- Keyboard shortcut to show/hide our sidebar
$(window).keydown (keyboardShortcutHandler);

function keyboardShortcutHandler (zEvent) {
    //--- On F9, Toggle our panel's visibility
    if (zEvent.which == 120) {  // F9
        kb1ShortcutFired = true;

        if (sfrpSideBar.is (":visible") ) {
            sfrpSideBar.stop (true, false).hide ();
        }
        else {
            //-- Reappear opaque to start
            sfrpSideBar.stop (true, false).show ();
            sfrpSideBar.fadeTo (0, 1);
            sfrpSideBar.fadeTo (2900, 0.1);
        }

        zEvent.preventDefault ();
        zEvent.stopPropagation ();
        return false;
    }
}

