            function Archive() {//{{{
                this.hitGroups = {};
                this.ratings = { global: { comm: 0, pay: 0, fair: 0, fast: 0, reviews: 0 } };
                this.prune = function() {
                    var keepAlive = 3600 /* 60 mins */, threshold = Date.now() - keepAlive * 1000;
                    this.filter(v => v.purge < threshold).forEach(v => delete this.hitGroups[v.groupId]);
                };
                this.add = function(key, value) {
                    var anchor = document.querySelector('#resultsTable tbody').rows.length;
                    if (!(key in this.hitGroups)) { // new entry
                        if (value.requester.id && value.requester.id in this.ratings)
                            value.TO = this.ratings[value.requester.id];
                        this.hitGroups[key] = value;
                        this.hitGroups[key].isNew = anchor ? true : false;
                        this.hitGroups[key].shine = anchor ? true : false;
                    }
                    else { // existing entry
                        this.hitGroups[key].isNew = false;
                        var age = Math.floor((Date.now() - this.hitGroups[key].discovery)/1000);
                        this.hitGroups[key].shine = this.hitGroups[key].shine && age < +Settings.user.shine && anchor ? true : false;
                        for (var k of ['blocked', 'included', 'index', 'numHits']) this.hitGroups[key][k] = value[k];
                    }
                    this.hitGroups[key].purge = value.discovery;
                    this.hitGroups[key].current = true;
                };
                this.filter = function(callback, ridsOnly) {
                    ridsOnly = ridsOnly || false;
                    var _results = [];
                    for (var k in this.hitGroups) { if (this.hitGroups.hasOwnProperty(k)) {
                        if (callback(this.hitGroups[k], k, this.hitGroups)) 
                            _results.push( ridsOnly ? this.hitGroups[k].requester.id : this.hitGroups[k] );
                    }}
                    return _results;
                };
                this.addReviews = function(obj) {
                    if (!obj) return;
                    var groups = this.filter(v => v.current && v.TO === null),
                        rids = Object.keys(obj),
                        adj = (x,n) => ((x*n+15)/(n+5)) - 1.645*Math.sqrt((Math.pow(1.0693*x,2) - Math.pow(x,2))/(n+5));
                    for (var k of rids) {
                        if (typeof obj[k] === 'string') continue; // no reviews yet

                        // adjust ratings
                        var _n=0, _d=0, attr;
                        for (attr of Object.keys(obj[k].attrs)) {
                            _n += obj[k].attrs[attr]*Settings.user.toWeights[attr]; 
                            _d += +Settings.user.toWeights[attr];
                        }
                        obj[k].attrs.qual = (_n/_d).toPrecision(4);
                        obj[k].attrs.adjQual = adj(_n/_d, +obj[k].reviews).toPrecision(4);
                        obj[k].attrs.adjPay = adj(+obj[k].attrs.pay, +obj[k].reviews).toPrecision(4);

                        // aggregate globals
                        if (k in this.ratings) continue; // prevent aggregating known values
                        this.ratings[k] = obj[k];
                        this.ratings.global.reviews += obj[k].reviews;
                        for (attr of Object.keys(obj[k].attrs)) this.ratings.global[attr] += obj[k].attrs[attr] * obj[k].reviews;
                    }
                    for (var g of groups) {
                        if (obj[g.requester.id] && obj[g.requester.id].name !== g.requester.name) 
                            obj[g.requester.id].name = g.requester.name;
                        this.hitGroups[g.groupId].TO = obj[g.requester.id]; // empty string if no TO
                    }
                };
                Object.defineProperties(this, { 
                    length: { get: () => Object.keys(this.hitGroups).length },
                    keys: { get: () => Object.keys(this.hitGroups) },
                    globalPay: { get: () => this.ratings.global.reviews ? (this.ratings.global.pay/this.ratings.global.reviews).toPrecision(4) : 0},
                    globalQuality: { get: () => {
                        if (!this.ratings.global.reviews) return 0;
                        var attrs = ['comm','pay','fast','fair'], _result = 0, _d = 0;
                        for (var a of attrs) { 
                            _result += this.ratings.global[a]*Settings.user.toWeights[a]/this.ratings.global.reviews; 
                            _d += +Settings.user.toWeights[a];
                        }
                        return (_result/_d).toPrecision(4);
                    }}
                });
            }//}}}

