	var widthItem = function( name ){
		switch(name){
			case('Кольца'): return 25;
			case('Кастеты'):
			case('Ножи и кинжалы'): 
			case('Мечи'):
			case('Топоры'):
			case('Булавы'):
			case('Дубины'):
			case('Молоты'):
			case('Посохи и скипетры'):
			case('Кнуты'):
			case('Алебарды и копья'):
			case('Метательное оружие'):
			case('Стрелковое оружие'):
			case('Серпы'):
			case('Щиты'): return 55;
			default: return 40;
		}
    };
	var txtItem = function( name ){
		var txt = '', num = 0;
		for (key in Items) {
			if(Items[key][4] == name) {
				num++;
				if(num == 1) txt += '<tr>';
				txt += '<td class="tdItem" name="' + key + '"><img src="' + Items[key][2] + '" width=' + widthItem(name) + '><br>' + Items[key][1] + '</td>'
				if(num == 8) { txt += '</tr>'; num = 0; }
			}
		}
		return txt;
	};

