// ==UserScript==
// @name            迅雷离线下载
// @description     在Firefox网页中添加迅雷离线下载右键菜单，可通过选中eMule/磁力链通过右键离线下载。
// @version         17.06.01.1
// @author          nftbty
// @include         *
// @exclude         file:///*
// @exclude         /^http(s?)://lixian.vip.xunlei.com/.*/
// @namespace       https://greasyfork.org/users/101499
// @downloadURL https://update.greasyfork.org/scripts/30198/%E8%BF%85%E9%9B%B7%E7%A6%BB%E7%BA%BF%E4%B8%8B%E8%BD%BD.user.js
// @updateURL https://update.greasyfork.org/scripts/30198/%E8%BF%85%E9%9B%B7%E7%A6%BB%E7%BA%BF%E4%B8%8B%E8%BD%BD.meta.js
// ==/UserScript==
var SelectTxt = '';
var objReg = /^(magnet\:\?xt\=).*|^(ed2k\:\/\/).*|(.*)\.torrent$/;
if (window.getSelection) {
  SelectTxt = window.getSelection();
} else {
  SelectTxt = document.selection.createRange().text;
}
var menu = document.body.appendChild(document.createElement('menu'));
menu.outerHTML = '<menu id="userscript-context-menu" type="context">\
                     <menuitem label="" icon="data:image/png;base64,">\
                     </menuitem>\
                  </menu>';
var html = document.documentElement;
html.setAttribute('contextmenu', 'userscript-context-menu');

if ('contextMenu' in html && 'HTMLMenuItemElement' in window) {
  $('#userscript-context-menu menuitem').addEventListener('click', thunder, false);
  html.addEventListener('contextmenu', initMenu, false);
}
function initMenu(e) {
  var node = e.target;
  var title = document.title;

  var menu = $("#userscript-context-menu menuitem");
  if (objReg.test(SelectTxt)) {
    menu.label = "迅雷离线下载选中链接";
  } else {
    menu.label = "进入迅雷离线";
  }
  var canonical = $("head link[rel='canonical']");
  var url = canonical ? canonical.href: location.href;

  if (node && node.hasAttribute('href')) {
    menu.label = '迅雷离线下载';
    url = node.href;
    title = e.target.getAttribute('alt') || node.textContent;
  }
  menu.title = title;
  menu.setAttribute('url', url);
}

function thunder(e) {
  var title = encodeURIComponent(e.target.title);
  var url = encodeURIComponent(e.target.getAttribute('url'));
  var name = 'Thunder_offLine_Donwload';
  var feature = 'width=900, height=650, menubar=no, toolbar=no, location=no, scrollbars=yes, status=no, modal=yes';
  switch (e.target.label) {
  case "迅雷离线下载":
    open('http://lixian.vip.xunlei.com/?furl=' + url, '_blank');
    break;
  case "迅雷离线下载选中链接":
    if (SelectTxt !== '' && SelectTxt !== null) {
      open('http://lixian.vip.xunlei.com/?furl=' + SelectTxt, '_blank');
    }
    break;
  default:
      //open('http://lixian.vip.xunlei.com/', name, feature);
      open('http://lixian.vip.xunlei.com/', '_blank');
  }
}
function $(aSelector, aNode) {
  return (aNode || document).querySelector(aSelector);
}