			commentArea.val(comment).change();

			commentArea.keyup();

			if(!$('.no-comments')) {
				commentArea.css("backgroundColor","Yellow");
			}

			// Click the UR status
			setTimeout(checkIfClickStatus(urStatus, autoSendComment), 100);
