        function calc_timer(task) {
            // task = {
            //     from: {
            //         coord: [0,0],
            //         arena: 0,
            //         nation: 0
            //     },
            //     to: {
            //         coord: [0,0],
            //         time: 0 //Date
            //     }
            // }
            let distance = Math.pow(Math.pow(task.from.coord[0] - task.to.coord[0], 2) + Math.pow(task.from.coord[1] - task.to.coord[1], 2), 1 / 2);
            let time_diff = (task.to.time - new Date(Date.now())) / 1000;
            let real_time = []; // time_diff - time_troop;
            dict_troops_index[PLAYER.nation].vel.forEach(speed=>{
                if(distance > 20){
                    let time_troop_1 = 20 / speed * 3600; //sec (*3600)
                    let time_troop_2 = (distance-20) / (speed*(1+0.1*task.from.arena)) * 3600; //sec (*3600)
                    real_time.push(parseInt(time_diff - time_troop_1 - time_troop_2));
                }else{
                    let time_troop = distance / speed * 3600; //sec (*3600)
                    real_time.push( parseInt(time_diff - time_troop) );
                }
            });
            return real_time;
        }
        function time2str(time){
            let result = time >= 0 ? '+ ' : '- ';
            let el = [
                parseInt(Math.abs(time / 3600)),
                parseInt(Math.abs(time % 3600 / 60)),
                Math.round(Math.abs(time % (3600 / 60)))
            ];
            result += el[0] > 9 ? el[0] : '0' + el[0] ;
            result += ':';
            result += el[1] > 9 ? el[1] : '0' + el[1];
            result += ':';
            result += el[2] > 9 ? el[2] : '0' + el[2];
            return result;
        }
        function create_helper_info(parent, coord, target, active) { // coord = {from: [?,?] to: [?,?]}
            if ($('.selectVillage')[0].options.selectedIndex == -1) return;
            let timers_start = calc_timer({
                from: PLAYER,
                to: {
                    coord: coord,
                    time: target
                }
            });
            let div = $('<div>', {
                class: 'DKHelperInfo',
                css: {
                    width: '8em',
                    height: 'auto',
                    textAlign: 'center',
                    // width: 400,
                    // height: 300,
                    border: '1px solid black'
                }
            })
            let p_troops = [];
            for (let i = 0; i < dict_troops_index[PLAYER.nation].active().length; i++) {
                p_troops.push($('<p>', {
                    css: {
                        margin: '0px auto 3px',
                        backgroundColor: timers_start[dict_troops_index[PLAYER.nation].active()[i]] >= 0 ? 'greenyellow' : 'lightcoral'
                    }
                }).append(
                    $('<img>', {
                        src: dict_troops_index[PLAYER.nation].src[dict_troops_index[PLAYER.nation].active()[i]]
                    }),
                    $('<span>', {
                        class: 'infotimer',
                        css: {
                            marginLeft: 10
                        },
                        text: time2str(timers_start[dict_troops_index[PLAYER.nation].active()[i]])
                    })
                ));
            }
            div.append(p_troops).appendTo(parent);
            if (!active) {
                return;
            }
            intervalIds.push( setInterval(el => {
                //if ($('.selectVillage')[0].options.selectedIndex == -1) clearInterval(tid);
                let timers = calc_timer({
                    from: PLAYER,
                    to: {
                        coord: coord,
                        time: target
                    }
                });
                $(el).each((i, v) => {
                    let child_el = $(v).find('.infotimer');
                    timers[dict_troops_index[PLAYER.nation].active()[i]] >= 0 ? child_el.parent().css('background-color', 'greenyellow') : child_el.parent().css('background-color', 'lightcoral');
                    child_el.text(time2str(timers[dict_troops_index[PLAYER.nation].active()[i]]));
                });
            }, 1000, p_troops));
        }

