        function main() {
            coords_targets = [];
            intervalIds.forEach(v=>{
                clearInterval(v);
            });
            intervalIds.splice(0, intervalIds.length);
            PLAYER.coord = (() => {
                if ($('.selectVillage')[0].options.selectedIndex == -1) return [0, 0];
                let obj = load()
                return [obj[$('.selectVillage')[0].options.selectedIndex].x, obj[$('.selectVillage')[0].options.selectedIndex].y];
            })();
            PLAYER.arena = (() => {
                if ($('.selectVillage')[0].options.selectedIndex == -1) return 0;
                let obj = load()
                return obj[$('.selectVillage')[0].options.selectedIndex].arena;
            })();
            PLAYER.nation = (()=>{
                return loadPlayer();
            })();
            $('.DKHelperInfo').remove();
            let tr = $('#tableList > tbody:nth-child(2) > tr');
            tr.each((i, v) => {
                let coord = $(v).find('td > div > a').attr('href').match(/x=([-\d]*).*y=([-\d]*)/).slice(1).map(e => { return parseInt(e) });
                let parent_inj = $(v).find('td:nth-child(2)');
                let timer_parse = parent_inj.text().split(/[ /:]/).map(e => { return parseInt(e) });
                let date_target = new Date(2000 + timer_parse[2], timer_parse[1] - 1, timer_parse[0], timer_parse[3], timer_parse[4], timer_parse[5]);
                coords_targets.push([coord, date_target]);
                create_helper_info(parent_inj, coord, date_target, true);
            });
        }

