        function create_units_element(){
            let row1 = $('<tr>');
            let row2 = $('<tr>');
            let ma = loadActive() || dict_troops_index[loadPlayer()].active();//dict_troops_index[PLAYER.nation].active();
            for(let i in dict_troops_index[loadPlayer()].src){
                row1.append(
                    $('<td>').append($('<img>',{src: dict_troops_index[loadPlayer()].src[i]}))
                );
                row2.append(
                    $('<td>').append($('<input>',{
                        type: 'checkbox',
                        on: {
                            click: ()=>{
                                let buffer_active = [];
                                $('.checkUnits input').each((i,v)=>{
                                    if($(v).is(':checked')) buffer_active.push(i);
                                });
                                saveActive(buffer_active);
                                main();
                            }
                        }
                    }).prop('checked', (()=>{
                        if(ma.indexOf(parseInt(i)) != -1){ return true; }else{ return false; }
                           })()))
                );
            }
            $('<table>',{class: 'checkUnits'}).append([row1, row2]).appendTo('.DKsettingsGlobal');
        }
