setTimeout(initMapRaidOverlay, 1000);
var mapLayer,
	displayedArea = false;

function convertPoints(list) {
	return list.map(function(point) {
		return new OpenLayers.Geometry.Point(point[0], point[1]).transform(new OpenLayers.Projection("EPSG:4326"), W.map.getProjectionObject());
	});
}

function addRaidPolygon(raidLayer, groupPoints, color, name) {
	var style = {
		strokeColor: "Pink",
		strokeOpacity: 0.8,
		strokeWidth: 3,
		fillColor: '#FFFFFF', // Doesn't matter, opacity is set to 0
		fillOpacity: 0,
		label: name,
		labelOutlineColor: "Black",
		labelOutlineWidth: 3,
		fontSize: 14,
		fontColor: "Pink",
		fontOpacity: 0.85,
		fontWeight: "bold"
	};

	var ring = new OpenLayers.Geometry.LinearRing(convertPoints(groupPoints));
	var polygon = new OpenLayers.Geometry.Polygon([ ring ]);

	var feature = new OpenLayers.Feature.Vector(polygon, { name: name }, style);
	raidLayer.addFeatures([ feature ]);
}

function createLayerToggler(parentGroup, checked, name, toggleCallback) {
	var normalizedName = name.toLowerCase().replace(/\s/g, '');
	var layerItem = document.createElement('li');
	var layerToggle = document.createElement('wz-checkbox');
	layerToggle.id = 'layer-switcher-' + normalizedName;
	layerToggle.textContent = name;
	layerItem.appendChild(layerToggle);
	parentGroup.querySelector('ul').appendChild(layerItem);
	parentGroup.querySelector('wz-toggle-switch').addEventListener('click', function(e) {
		layerToggle.disabled = !e.target.checked;
		toggleCallback && toggleCallback(layerToggle.checked && e.target.checked);
	});
	layerToggle.addEventListener('click', e => toggleCallback(e.target.checked));
	layerToggle.checked = checked;
	return layerItem;
}

function displayCurrentRaidLocation() {
	var raidMapCenter = W.map.getCenter();
	var raidCenterPoint = new OpenLayers.Geometry.Point(raidMapCenter.lon, raidMapCenter.lat);
	var locationDiv = document.querySelector('#topbar-container > div > div > div.location-info-region > div.location-info');
	var mapRaidDiv = locationDiv.querySelector('strong');
	if (mapRaidDiv === null) {
		mapRaidDiv = document.createElement('strong');
		mapRaidDiv.style.marginLeft = '5px';
		locationDiv.appendChild(mapRaidDiv);
	}

	for (i = 0; i < mapLayer.features.length; i++) {
		if (mapLayer.features[i].geometry.components[0].containsPoint(raidCenterPoint)) {
			mapRaidDiv.textContent = ' [Region: ' + mapLayer.features[i].attributes.name + ']';
			displayedArea = true;
			return;
		}
	}
	if (displayedArea) {
		mapRaidDiv.textContent = '';
		displayedArea = false;
	}
}

