// ==UserScript==
// @name         Auto refresh MFC
// @namespace    https://greasyfork.org
// @version      0.5
// @description  连接不上时自动刷新（默认间隔 5 秒），如果页面成功加载，以通知的方式进行提醒（不需要停留在 MFC 页面）；如果浏览器不支持，则以声音进行提醒（必须停留在 MFC 页面）
// @author       pot-code
// @include      *://www.mfcclub.com/*
// @include      *://mfcclub.com/*
// @include      *://www.mfcclub.info/*
// @include      *://www.mfcclub.net/*
// @grant        none
// @downloadURL https://update.greasyfork.org/scripts/37571/Auto%20refresh%20MFC.user.js
// @updateURL https://update.greasyfork.org/scripts/37571/Auto%20refresh%20MFC.meta.js
// ==/UserScript==

(function() {
    'use strict';

    var noti_sound = new Audio("data:audio/wav;base64,");
    var BEEP_INTERVAL=1500;//ms
    var BEEP_TIMES=2;
    var REFRESH_DELAY=2000;//ms
    var NOTI_SENTENCE='页面成功刷出';

    if(window.Notification){
        if(Notification.permission === 'default'){
            Notification.requestPermission(permission_dispatcher);
        }else{
            permission_dispatcher(Notification.permission);
        }
    }else{// fallback
        main(false);
    }

    // ================================
    // Core-function
    // ================================
    function permission_dispatcher(permission){
        if(permission==='granted'){
            main(true);
        }else{
            main(false);
        }
    }

    function play_sound(left){
        noti_sound.play();
        if(left>1){
            setTimeout(play_sound, BEEP_INTERVAL, --left);
        }
    }

    function main(is_noti_api_enabled){
        var hasError = Boolean(document.querySelector('.cf-wrapper.cf-error-overview'));
        var hasKeyElement=Boolean(document.querySelector('.login.main_container'));
        var isTransitionPage = Boolean(document.querySelector('#cf-content'));
        var isMaintaining=/maintenance/.test(window.location.href);

        if(isMaintaining){
            setTimeout(function(){
                window.location.href='https://www.mfcclub.com/login.jsp';
            },4000);
            return;
        }
        if(hasError||!hasKeyElement){
            setTimeout(function(){
                document.location.reload();
            },REFRESH_DELAY);
        }else if(!isTransitionPage){
            play_sound(BEEP_TIMES);
            if(is_noti_api_enabled){
                new Notification(NOTI_SENTENCE);
            }
        }
    }
})();