
// ==UserScript==
// @name                Chat Alerts
// @namespace           https://www.waze.com
// @description         This add-on alerts you if there are new messages
// @include             https://www.waze.com/editor*
// @include             https://www.waze.com/*/editor*
// @include             https://beta.waze.com/*
// @exclude             https://www.waze.com/user/editor*
// @version             2.6
// @downloadURL https://update.greasyfork.org/scripts/39131/Chat%20Alerts.user.js
// @updateURL https://update.greasyfork.org/scripts/39131/Chat%20Alerts.meta.js
// ==/UserScript==
//---------------------------------------------------------------------------------------

window.onload = function(){

    var index = 0;
    var list = [];

    setInterval(function(){
        
        // get class of message
        var d = document.getElementsByClassName("message normal-message ");
        //-----------------------------------------------------------------

        // check if length equal zero
        if (d.length == 0){
            if (document.getElementById("divNav") != null){
                document.getElementById("divNav").remove();
                index = 0;
                return false;
            }
        }
        //-------------------------------------------------

        for (i = 0; i < d.length; i++){

            // check if class name equal message normal
            if (d[i].className == "message normal-message " || d[i].className == "message normal-message"){

                for (x = 0; x < list.length; x++){
                    if (list[x].includes(d[i].innerText)){
                        return false;
                    }
                }

                // check if divNav not equal null
                if (document.getElementById("divNav") != null){
                    index++;
                    document.getElementById("divNav").innerText = index;
                    list.push(d[i].innerText);
                    beep();
                }else{
                    var divNave = document.createElement("div");
                    divNave.setAttribute("id","divNav");
                    divNave.style.position = "absolute";
                    divNave.style.right = "-18%";
                    divNave.style.top = "-18%";
                    divNave.style.backgroundColor = "#f32334";
                    divNave.style.width = "15px";
                    divNave.style.height = "15px";
                    divNave.style.borderRadius = "100%";
                    divNave.style.textAlign = "center";
                    divNave.style.color = "#ffffff";
                    divNave.style.fontSize = "1px";
                    document.getElementById("chat-toggle").appendChild(divNave);
                    //---------------------------------------------------------
                    index++;
                    document.getElementById("divNav").innerText = index;
                    list.push(d[i].innerText);
                    beep();
                }
            }
        }
    },5);

    // when click on the icon of chat
    document.getElementById("chat-toggle").addEventListener("click",function(){
        if (document.getElementById("divNav") != null){
            index = 0;
            document.getElementById("divNav").remove();
        }
    });

    // function for the sound of nav
    function beep() {
        var snd = new Audio("data:audio/wav;base64,");  
        snd.play();
    }
}