// ==UserScript==
// @name         百度清爽模式，专注学习工作
// @namespace    http://tampermonkey.net/
// @version      2020.06.30
// @description  去首页新闻，去右侧搜索热点，去部分广告，搜索内容居中。学习工作不会被大数据推荐打扰，有帮助到您的话，小小打赏一下。
// @author       chanknight
// @match        *://www.baidu.com
// @match        *://www.baidu.com/s*
// @mail         chanknight666@gmail.com
// @grant        unsafeWindow

// @downloadURL https://update.greasyfork.org/scripts/394661/%E7%99%BE%E5%BA%A6%E6%B8%85%E7%88%BD%E6%A8%A1%E5%BC%8F%EF%BC%8C%E4%B8%93%E6%B3%A8%E5%AD%A6%E4%B9%A0%E5%B7%A5%E4%BD%9C.user.js
// @updateURL https://update.greasyfork.org/scripts/394661/%E7%99%BE%E5%BA%A6%E6%B8%85%E7%88%BD%E6%A8%A1%E5%BC%8F%EF%BC%8C%E4%B8%93%E6%B3%A8%E5%AD%A6%E4%B9%A0%E5%B7%A5%E4%BD%9C.meta.js
// ==/UserScript==

(function() {

    loadStyle()
    function loadStyle(css) {
        if(!css){
            css = '#content_right {display: none !important}';
            css+= '#content_left {padding-left:33% !important; padding-top:2% !important}';
            css+='#container {width:100% !important}'
            css+= '#page {padding-left:25% !important}';
            css+= '#rs {width:100%;padding-left:25%;margin: 6px 0 0 0px; !important}';
            css+= '#foot {display: none !important}';
            css+= '#s_tab {display: none !important}';
            css+= '#result_logo {display: none !important}';
            css+= '.qrcodeCon {display: none !important}';
            css+= '#rs table{margin:0}';
            css+= '#ftCon {display: none !important}';
            css+= '#s_wrap {display: none !important}';
            css+= '#bottom_layer {display: none !important}';
            css+= '.s-top-nav {display: none !important}';
            css+= '#ent_sug {display: none !important}';
        }

        var style = document.createElement('style');
        style.type = 'text/css';
        style.rel = 'stylesheet';
        style.appendChild(document.createTextNode(css));
        var head = document.getElementsByTagName('html')[0];
        head.appendChild(style);
    }


    function align_center(){
        loadStyle('.s_form_wrapper{display: flex;align-items: center;justify-content: center;}');
        document.getElementById('kw').removeEventListener('input',align_center);
    }
    document.getElementById('kw').addEventListener('input',align_center);

    function interval_fun(){
        if(location.href.indexOf('www.baidu.com/s') != -1){
            if(oldHref=="https://www.baidu.com/" || oldHref=="http://www.baidu.com/"){
                location.reload();
            }
            align_center();
            var date = localStorage.getItem(key);
            var vip = localStorage.getItem('_vip_');
            if(vip){
                if(Number(new Date().getTime()/1000) > Number(vip)) {
                    localStorage.removeItem('_vip_');
                    dashang();
                }
            }
            else{
                if(date==null || Number(new Date().getTime()/1000) > Number(date)) dashang();
            }

            interval && clearInterval(interval);
        }
    }

    let oldHref = location.href;
    var version = GM_info.script.version;
    var key = '_Date_' + version;
    var randomTime = (Math.floor(Math.random()*7)+1)*24*60*60;
    setInterval(()=>{
        console.log(location.href,oldHref)
        if(location.href != oldHref){
            location.reload()
        }
    },100)
    interval_fun();
    //var interval = setInterval(function(){
    //    interval_fun();
    //},100);

    function dashang(){
        var css = "";
        css += "	.content{float: center;}";
        css += "	.hide_box{z-index:999;filter:alpha(opacity=50);background:#666;opacity:0.5;-moz-opacity:0.5;left:0;top:0;height:99%;width:100%;position:fixed;display:none}";
        css += "	.shang_box{width:540px;height:540px;padding:10px;background-color:#fff;border-radius:10px;position:fixed;z-index:1000;left:50%;top:50%;margin-left:-280px;margin-top:-280px;border:1px dotted #dedede;display:none}";
        css += "	.shang_box img{border:none;border-width:0}";
        css += "	.dashang{display:block;width:100px;margin:5px auto;height:25px;line-height:25px;padding:10px;background-color:#E74851;color:#fff;text-align:center;text-decoration:none;border-radius:10px;font-weight:bold;font-size:16px;transition:all 0.3s}";
        css += "	.dashang:hover{opacity:0.8;padding:15px;font-size:18px}";
        css += "	.shang_close{float:right;display:inline-block}";
        css += "	.shang_logo{display:block;text-align:center;margin:20px auto}";
        css += "	.shang_tit{width:100%;height:75px;text-align:center;line-height:66px;color:#a3a3a3;font-size:16px;background:url('data:image/jpg;base64,');font-family:'Microsoft YaHei';margin-top:7px;margin-right:2px}";
        css += "	.shang_tit p{color:#a3a3a3;text-align:center;font-size:16px}";
        css += "	.shang_payimg{width:140px;padding:10px;border:6px solid #EA5F00;margin:0 auto;border-radius:3px;height:140px}";
        css += "	.shang_payimg img{display:block;text-align:center;width:140px;height:140px}";
        css += "	.pay_explain{text-align:center;margin:10px auto;font-size:12px;color:#545454}";
        css += "	.radiobox{width:16px;height:16px;background:url('data:image/jpg;base64,');display:block;float:left;margin-top:5px;margin-right:14px}";
        css += "	.checked .radiobox{background:url('data:image/jpg;base64,')}";
        css += "	.shang_payselect{text-align:center;margin:0 auto;margin-top:40px;cursor:pointer;height:60px;width:280px}";
        css += "	.shang_payselect .pay_item{display:inline-block;margin-right:10px;float:left}";
        css += "	.shang_info{clear:both}";
        css += "	.shang_info p,.shang_info a{color:#C3C3C3;text-align:center;font-size:12px;text-decoration:none;line-height:2em}";
        css += "	.shang_foot{padding-left: 100px;width: 400px;margin: 0 auto;margin-top: 20px;font-size:14px;}";
        css += "    .pay_item {margin-top: 3px;}"

        var content = "";
        content += "<div class='content'>";
        content += "	<p><a href='javascript:void(0)' onclick='dashangToggle()' class='dashang' title='打赏，支持一下'>打赏</a></p>";
        content += "	<div class='hide_box'></div>";
        content += "	<div class='shang_box'>";
        content += "		<a class='shang_close' href='javascript:void(0)' onclick='dashangToggle()' title='关闭'><img";
        content += "				src='data:image/jpg;base64,'";
        content += "				alt='取消' /></a>";
        content += "		<div class='shang_tit'>";
        content += "			<p>待业ing,渴望您的支持</p>";
        content += "		</div>";
        content += "		<div class='shang_payimg'>";
        content += "			<img id='shang_img' src='data:image/jpg;base64,'>";
        content += "		</div>";
        content += "		<div class='pay_explain'>扫码打赏，你说多少就多少</div>";
        content += "		<div class='shang_payselect'>";
        content += "			<div class='pay_item checked' data-id='alipay'>";
        content += "				<span class='radiobox'></span>";
        content += "				<span class='pay_logo'><img";
        content += "						src='data:image/jpg;base64,'";
        content += "						alt='支付宝' /></span>";
        content += "			</div>";
        content += "			<div class='pay_item' data-id='weipay'>";
        content += "				<span class='radiobox'></span>";
        content += "				<span class='pay_logo'><img";
        content += "						src='data:image/jpg;base64,'";
        content += "						alt='微信' /></span>";
        content += "			</div>";
        content += "		</div>";
        content += "		<div class='shang_info'>";
        content += "			<p>打开<span id='shang_pay_txt'>支付宝</span>扫一扫，即可进行扫码打赏</p>";
        content += "		</div>";
        content += "		<div class='shang_foot'>";
        content += "			<div class='pay_item' data-id='Try'>";
        content += "				<span class='radiobox'></span>";
        content += "				<span>🤗我先体验一下(随机隐藏打赏按钮1-7天)</span>";
        content += "			</div>";
        content += "			<div class='pay_item' data-id='Wait'>";
        content += "				<span class='radiobox'></span>";
        content += "				<span>💪囊中羞涩，一个月后再说(隐藏打赏按钮31天)</span>";
        content += "			</div>";
        content += "			<div class='pay_item' data-id='Free'>";
        content += "				<span class='radiobox'></span>";
        content += "				<span>🌚插件有帮助，但我是白嫖党(隐藏打赏按钮180天)</span>";
        content += "			</div>";
        content += "			<div class='pay_item' data-id='Donate'>";
        content += "				<span class='radiobox'></span>";
        content += "				<span>🍺好东西，我已打赏(隐藏打赏按钮365天)</span>";
        content += "			</div>";
        content += "		<div>";
        content += "	</div>";
        content += "</div>";

        var scriptcode = "";
        scriptcode += "	function dashangInit() {";
        scriptcode += "		$('.pay_item').click(function () {";
        scriptcode += "			$(this).addClass('checked').siblings('.pay_item').removeClass('checked');";
        scriptcode += "			var dataid = $(this).attr('data-id');";
        scriptcode += "			if (dataid == 'alipay') {";
        scriptcode += "				document.getElementById('shang_img').setAttribute('src', 'data:image/jpg;base64,');";
        scriptcode += "			}else if(dataid == 'weipay') {";
        scriptcode += "				document.getElementById('shang_img').setAttribute('src', 'data:image/jpg;base64,');";
        scriptcode += "			}else if(dataid == 'Try') {";
        scriptcode += "				localStorage.removeItem('_vip_');";
        scriptcode += "				localStorage.setItem('"+key+"',new Date().getTime()/1000+"+randomTime+");";
        scriptcode += "			}else if(dataid == 'Wait') {";
        scriptcode += "				localStorage.removeItem('_vip_');";
        scriptcode += "				localStorage.setItem('"+key+"',new Date().getTime()/1000+31*24*60*60);";
        scriptcode += "			}else if(dataid == 'Free') {";
        scriptcode += "				localStorage.removeItem('_vip_');";
        scriptcode += "				localStorage.setItem('"+key+"',new Date().getTime()/1000+90*24*60*60);";
        scriptcode += "			}else if(dataid == 'Donate') {";
        scriptcode += "				localStorage.setItem('_vip_',new Date().getTime()/1000+365*24*60*60);";
        scriptcode += "			}";
        scriptcode += "			$('#shang_pay_txt').text(dataid == 'alipay' ? '支付宝' : '微信');";
        scriptcode += "		});";
        scriptcode += "	};";
        scriptcode += "	function dashangToggle() {";
        scriptcode += "		$('.hide_box').fadeToggle();";
        scriptcode += "		$('.shang_box').fadeToggle();";
        scriptcode += "	};";
        scriptcode += "	dashangInit();";


        var kw = document.getElementById('kw').value;
        var body = document.getElementsByTagName('body')[0];
        body.innerHTML += content;
        document.getElementById('kw').value = kw;

        var style = document.createElement('style');
        style.type = 'text/css';
        style.rel = 'stylesheet';
        style.appendChild(document.createTextNode(css));
        var head = document.getElementsByTagName('html')[0];
        head.appendChild(style);

        var script = document.createElement('script');
        script.type = 'text/javascript';
        script.appendChild(document.createTextNode(scriptcode));
        head.appendChild(script);
    }
})();