  // è·å–ä¸€è¨€
  function getHitokoto(){
    let msg;
    let author;
    let httpRequest;
    try{
      if (window.XMLHttpRequest){// code for IE7, Firefox, Mozilla, etc.
      httpRequest=new XMLHttpRequest();
      }else if (window.ActiveXObject){// code for IE5, IE6
        httpRequest=new ActiveXObject("Microsoft.XMLHTTP");
      }
      httpRequest.open('GET', 'https://v1.hitokoto.cn/', true);
      httpRequest.send();
      httpRequest.onload = function () {
        if (httpRequest.readyState == 4 && httpRequest.status == 200) {
          let json = httpRequest.responseText;
          let jsonObject = eval('(' + json + ')');
          msg = jsonObject.hitokoto;
          author = jsonObject.from;
          let spaceLength = Math.floor(msg.length * 2);
          let space = new Array(spaceLength).join(" ");
          window.hitokoto = `${msg}\n${space}-${author}`;
        }else{
          alert(`HTTPè¯·æ±‚å¤±è´¥,é”™è¯¯åŸå› : è¯·æ±‚çš„URLçš„ç›¸åº”çŠ¶æ€=${httpRequest.status}\nè¯·æ±‚çš„ç»“æœçŠ¶æ€=${httpRequest.readyState}`);
        }
      };
      httpRequest.onerror = function(){
         alert(`HTTPè¯·æ±‚å¤±è´¥,é”™è¯¯åŸå› : è¯·æ±‚çš„URLçš„ç›¸åº”çŠ¶æ€=${httpRequest.status}\nè¯·æ±‚çš„ç»“æœçŠ¶æ€=${httpRequest.readyState}`);
      }
    }catch(err){
      debugging(`-->HTTPè¯·æ±‚å¤±è´¥, æœªçŸ¥é”™è¯¯<--`);
    }
    
  }

  // æ·»åŠ å­—ç¬¦ä¸²formatæ–¹æ³•
  String.prototype.format = function(args) {
    var result = this;
    if (arguments.length > 0) {
        if (arguments.length == 1 && typeof (args) == "object") {
            for (var key in args) {
                if(args[key]!=undefined){
                    var reg = new RegExp("({" + key + "})", "g");
                    result = result.replace(reg, args[key]);
                }
            }
        }
        else {
            for (var i = 0; i < arguments.length; i++) {
                if (arguments[i] != undefined) {
                    //var reg = new RegExp("({[" + i + "]})", "g");//è¿™ä¸ªåœ¨ç´¢å¼•å¤§äº9æ—¶ä¼šæœ‰é—®é¢˜
                    var reg = new RegExp("({)" + i + "(})", "g");
                    result = result.replace(reg, arguments[i]);
             }
          }
       }
    }
    return result;
  }

