  function menu_Quick(){
    let box = document.querySelector("#box");
    let msg;
    if(!box){
      let quickHTML = '<div id="box" style="position: fixed; top: 105px; left: 1022px; padding: 10px; background: rgb(255, 255, 255) none repeat scroll 0% 0%; border-radius: 10px; z-index: 9; cursor: default; border: 1px solid;"><input type="button" value="X" id="close" style="border: 1px solid;background: rgba(255, 255, 255,0) none repeat scroll 0% 0%;padding: 0;float: right;width: 20px;border-radius: 5px;text-align: center;"><h1>å¿«æ·è¯„è®ºç®¡ç†</h1><textarea id="text" autofocus="true" style="width: 250px;height: 200px;border-radius: 5px;border: 1px solid;"></textarea><br><input type="button" value="é‡ç½®" id="reset" style="border: none;background: rgba(255, 255, 255,0) none repeat scroll 0% 0%;padding: 0;margin: 0;">&nbsp;&nbsp;<input type="button" value="ä¿å­˜" id="save" style="border: none;background: rgba(255, 255, 255,0) none repeat scroll 0% 0%;padding: 0;margin: 0;"></div>';
      let body = document.querySelector("body");
      body.innerHTML = quickHTML + body.innerHTML;
      box = document.querySelector("#box");
      let left = GM_getValue("boxLeft");
      let top = GM_getValue("boxTop");
      if(left || top){
        box.style.left = left + 'px';
        box.style.top = top + 'px';
      }
      let text = document.querySelector("#text");
      msg = GM_getValue('msg');
      text.value = msg.join("\n");

      // -----æ‹–æ‹½äº‹ä»¶-------
      let x,y,isDown = false;
      //é¼ æ ‡æŒ‰ä¸‹äº‹ä»¶
      box.addEventListener('mousedown',mousedownbox);
      function mousedownbox(e) {
        //è·å–xåæ ‡å’Œyåæ ‡
        x = e.clientX;
        y = e.clientY;

        //è·å–å·¦éƒ¨å’Œé¡¶éƒ¨çš„åç§»é‡
        left = box.offsetLeft;
        top = box.offsetTop;
        //å¼€å…³æ‰“å¼€
        isDown = true;
        //è®¾ç½®æ ·å¼
        box.style.cursor = 'move';
      }
      //é¼ æ ‡ç§»åŠ¨
      box.addEventListener('mousemove',mousemovebox);
      function mousemovebox(e) {
        if (isDown == false) {
            return;
        }
        //è·å–xå’Œy
        let nx = e.clientX;
        let ny = e.clientY;
        //è®¡ç®—ç§»åŠ¨åçš„å·¦åç§»é‡å’Œé¡¶éƒ¨çš„åç§»é‡
        let nl = nx - (x - left);
        let nt = ny - (y - top);

        box.style.left = nl + 'px';
        box.style.top = nt + 'px';

        GM_setValue("boxLeft",nl);
        GM_setValue("boxTop",nt);
      }
      //é¼ æ ‡æŠ¬èµ·äº‹ä»¶
      box.addEventListener('mouseup',mouseupbox);
      function mouseupbox() {
        //å¼€å…³å…³é—­
        isDown = false;
        box.style.cursor = 'default';
      }
      function noMove(event){
        debugging("-->è§¦å‘ä¸é¼ æ ‡ç§»åŠ¨äº‹ä»¶<--");
        isDown = false;
      }

      // -----æŒ‰é’®å…³é—­äº‹ä»¶-----
      let closeButton = document.querySelector("#close");
      let resetButton = document.querySelector("#reset");
      let saveButton = document.querySelector("#save");
      let button = document.querySelector("#action");

      closeButton.addEventListener("click", boxClose);
      resetButton.addEventListener("click", boxReset);
      saveButton.addEventListener("click", boxSave);

      closeButton.addEventListener("mousemove", noMove);
      resetButton.addEventListener("mousemove", noMove);
      saveButton.addEventListener("mousemove", noMove);
      text.addEventListener("mousemove", noMove);

      button.addEventListener("click", actionRandom);

    }else{
      box.hidden = !box.hidden;
      let text = document.querySelector("#text");
      msg = GM_getValue('msg');
      text.value = msg.join("\n");
    }
  }
