  function showReviewText(name){
    
    if(name in reviews){
      let rv = reviews[name];
      //console.log('review:',rv);
      $("#zone-review").text('Rating: ' + rv["Rate"] + '. ' + name + '. ' + rv["Desc"]); 
    } else {
      $("#zone-review").text('no comment!'); 
    }
    
    if(name in reviews2){
      let rv = reviews2[name];
      //console.log('review2:',rv);
      $("#bai-review").text(rv["Rate"] + '。' + name + '。' + rv["Desc"] + ' 有炸弹选炸弹，没炸弹先进绿 再进黑 白 蓝，红色 是辣鸡。 PS: 红黑崛起了,  红色是辣鸡这句话 我收回。'); 
    } else {
      $("#bai-review").text('此牌有玄机！欲知详情请备好礼物与我面谈！'); 
    }
    
  }

  function getZoneRating(name){
    if(name in reviews){
      let rv = reviews[name];
      return rv["Rate"];
    } else {
      return '?';
    }  
  }

  function getLiuRating(name){
    if(name in reviews2){
      let rv = reviews2[name];
      return rv["Rate"];
    } else {
      return '玄';
    }  
  }

  function getBaiRating(name){
    return '';
    if(name in map2){
      let idx = map2[name];
      let rv = reviews2[idx];
      return rv[1];
    } else {
      return '玄';
    }  
  }

  function getColorClass(rate){
    let cc;
    switch(rate){
        case "A+":
        case "A":
        case "5.0":
        case "4.5":
          cc = "t1";
          break;
        case "A-":
        case "B+":
        case "4.0":
          cc = "t2";
          break;
        case "B":
        case "B-":
        case "C+":
        case "3.5":
          cc = "t3";
          break;
        case "C":
        case "C-":
        case "3.0":
          cc = "t4";
          break;
        case "D+":
        case "2.5":
          cc = "t5";
          break;
        case "D":
        case "2.0":
        case "3*":
          cc = "t6";
          break;
        case "D-":
        case "1.5":
          cc = "t7";
          break;
        case "F":
        case "F+":
        case "F-":
        case "1.0":
        case "0.5":
        case "0.0":
          cc = "t8";
          break;
        
        default:
          cc = "tno";
    }
    //console.log('ColorClass:',cc);
    
    return cc;
  }


  





