  // ------------- 所有成语 ----------------


  // 韵母
  /**
   * @type {Array<Guess>}
   */
  let finals = []

  // 声母
  /**
   * @type {Array<Guess>}
   */
  let initials = []

  // 文字
  /**
   * @type {Array<Guess>}
   */
  let chars = []

  // 不需要音调, 音调的 spellInfo 有些字会报错 如瞋
  let tones = []

  // 保存之前所有的猜的成语
  let allGuesses = []

  let all = ALL_IDIOM

  const pinyinInitials = 'b p m f d t n l g k h j q r x w y zh ch sh z c s'.split(/\s/g)

  // 显示成语的列表
  let idiomList
  let wrapper
  const getConfig = () => ({
    itemHeight: 30,
    total: all.length,
    generate: function (index) {
      const origin = all[index][0]
      const el = document.createElement('div');
      el.setAttribute('flex', '~ gap-4')
      el.innerHTML = `
        <p>${all[index][0]}</p>
        <button class="btn" data-value="${origin}">复制</button>
      `
      return el;
    },
    width: 200,
    height: 200
  })

  function guess() {
    // 获取反馈信息, 更新三个表
    updateRules()

    // 过滤一下
    all = all.filter((item) => {
      return (
        check(item[0], chars, (value, rule) => value === rule) &&
        check(item[1], initials, (value, rule) => value === rule) &&
        check(item[2], finals, (value, rule) => value === rule)
        // 这里类库的音调是数字，dom读取的是字符
        // 由于类库返回的音调不是很准确 如见风使舵 故不检测音调
        // check(item[3], tones, (value, rule) => value == rule)
      )
    })

    // 更新列表
    idiomList.refresh(wrapper, getConfig())

    // 随机抽取一个
    const index = Math.floor(Math.random() * all.length)
    const item = all[index]
    console.log(all.slice(0, 100).map((item) => item[0]))

    navigator.clipboard.writeText(item[0])
  }

  function getFinalEleText(ele) {
    //<div mx-1px="" flex=""><div relative=""><div class="">u</div></div></div>
    // 类似这种结构
    // 只要获取每个子元素的第一个元素的文字
    // 如果碰到u，则判断兄弟是不是两点即可（其实也不用）
    let result = ''
    for (let i = 0; i < ele.children.length; i++) {
      const single = ele.children[i]
      const one = single.children[0].innerText
      if (one === 'u' && single.children.length === 2) {
        // 到这里假定已经使用数字音调了
        // 所以除了u还有另一个元素，那肯定是v了
        result += 'v'
      } else {
        result += one
      }
    }

    return result
  }

  function updateRules() {
    resetRules()
    // 去获取下规则
    const elements = document.querySelectorAll(
      'div[pt4][items-center] > div[flex=""]'
    )
    if (elements.length < 2) return false

    let right = 0
    let showToneTip = true
    for (let i = 0; i < elements.length - 1; i++) {
      /**
       * @type { Element }
       */
      const element = elements[i]

      // 校验是否是成语element
      if (element.children.length !== 4) continue


      let currentIdiom = ''

      // 四个字
      for (let i = 0; i < 4; i++) {
        const box = element.children[i].children[1]
        const charText = box.children[0]
        if (!charText) {
          break
        }
        // 判断文字在对应的位置
        const curText = charText.innerText
        currentIdiom += curText
        let flag = ''
        if (box.classList.contains('bg-ok')) {
          updateSingleRule(chars, i, curText, 'ok')
          flag = 'ok'
          // 计数
          right++
        } else if (charText.classList.contains('text-mis')) {
          // 文字对了, 但是位置不对
          updateSingleRule(chars, i, curText, 'mis')
          flag = 'mis'
        } else {
          // 没有这个字
          updateSingleRule(chars, i, curText, 'no')
        }

        // 例如ABCA， 0位置是正确的 3是错误，此时也要处理A下面的音调，
        // 否则到3的时候，会将该音调设置为不存在
        const temp = box.children[1].children[0].children
        let initialEle = temp[0]
        let finalEle = temp[1]
        let toneEle = temp[2]

        const initialText = initialEle.innerText.replace(/\s/g, '')

        let oneFinalEle = null
        if (!pinyinInitials.includes(initialText)) {
          finalEle = temp[0]
          oneFinalEle = finalEle
          toneEle = temp[1]
          initialEle = null
        } else {
          oneFinalEle = finalEle.children[0].children[0]
        }

        const svgs = finalEle.querySelectorAll('svg')
        // svg可能是音调或者是 u上面的两点
        // 排除两点后还有其他svg，其认为当前是音调模式
        // <svg><circle /><circle /></svb>
        if (showToneTip && [...svgs].filter(svg => svg.querySelectorAll('circle').length !== 2).length > 0) {
          alert('请先在设置中, 调成"数字声调"')
          showToneTip = false
        }
        // const finalText = finalEle.innerText.replace(/\s/g, '')
        const finalText = getFinalEleText(finalEle)
        console.log('匹配每一个字', curText, initialText, finalText)

        if (initialEle) {
          if (flag === 'ok' || initialEle.classList.contains('text-ok')) {
            updateSingleRule(initials, i, initialText, 'ok')
          } else if (flag === 'mis' || initialEle.classList.contains('text-mis')) {
            updateSingleRule(initials, i, initialText, 'mis')
          } else {
            updateSingleRule(initials, i, initialText, 'no')
          }
        }

        if (flag === 'ok' || oneFinalEle.classList.contains('text-ok')) {
          updateSingleRule(finals, i, finalText, 'ok')
        } else if (flag === 'mis' || oneFinalEle.classList.contains('text-mis')) {
          updateSingleRule(finals, i, finalText, 'mis')
        } else {
          updateSingleRule(finals, i, finalText, 'no')
        }
      }

      allGuesses.push(currentIdiom)
    }
  }

  /**
   *
   * @param {Array<Guess>} array
   * @param {number} index
   * @param {string} value
   * @param { 'ok' | 'mis' | 'no' } flag
   */
  function updateSingleRule(array, index, value, flag) {
    const idx = array.findIndex((item) => item.value === value)

    // 文字, 位置都正确
    if (flag === 'ok') {
      if (idx > -1) {
        array[idx].no = false
        addValue(array[idx].indeed, index)
      } else {
        array.push({
          include: [],
          exclude: [],
          indeed: [index],
          value,
        })
      }
    } else if (flag === 'mis') {
      if (idx > -1) {
        array[idx].no = false
        addValue(array[idx].exclude, index)
        deleteValue(array[idx].include, index)
      } else {
        array.push({
          indeed: [],
          exclude: [index],
          include: [0, 1, 2, 3].filter((item) => item !== index),
          value,
        })
      }
    } else {
      if (idx === -1) {
        // 不存在的
        array.push({
          value,
          no: true,
          include: [],
          exclude: [],
          indeed: [],
        })
      }
    }
  }

  function resetRules() {
    initials = []
    finals = []
    chars = []
    tones = []
    allGuesses = []
    all = ALL_IDIOM
  }

  /**
   *
   * @param {Array<string>} array 一个成语数组 如 ['掩', '耳', '盗', '铃']
   * @param {Array<Guess>} rules
   * @param {(value: string, rule: string) => boolean} callback 该回调判断成语里的某个值什么时候满足, 如文字的那就是相等, 韵母的就是includes等等
   */
  function check(array, rules, callback) {
    if (rules.length === 0) return true
    return rules.every((rule) => {
      // 每条规则都要满足
      let { include = [], exclude = [], indeed = [], no = false, value } = rule

      if (no) {
        exclude = [0, 1, 2, 3]
      } else {
        // 没有一个位置是满足的
        if (
          include.length > 0 &&
          !include.some((idx) => callback(array[idx], value))
        ) {
          return false
        }

        // indeed的位置有个位置不满足， 就直接gg
        if (
          indeed.length > 0 &&
          indeed.some((idx) => !callback(array[idx], value))
        ) {
          return false
        }
      }

      // 有一个满足的, 出现再错误的位置
      if (
        exclude.length &&
        exclude.some((idx) => callback(array[idx], value))
      ) {
        return false
      }

      return true
    })
  }

  // 工具类
  function addValue(array, value) {
    if (array.indexOf(value) === -1) {
      array.push(value)
    }
  }

  function deleteValue(array, value) {
    const idx = array.indexOf(value)
    if (idx > -1) {
      array.splice(idx, 1)
    }
  }

  function showRules() {
    console.log('=====================规则=====================')
    const str = `
    let initials = ${JSON.stringify(initials)};
    let finals = ${JSON.stringify(finals)};
    let chars = ${JSON.stringify(chars)};
    let tones = ${JSON.stringify(tones)};
    `
    console.log(str)
    console.log('=====================猜的=====================')
    console.table(allGuesses)
  }

