const baseUrl = 'https://user.fm210.cn/api/wk';
// '';04f8d032c904f1029bc6a8c18347ca43
const token = '8b9ebae8224e9db7debc8c8fd66f8df2';

let hasRun = false;

waitForKeyElements('.list-box', () => {

	if (hasRun) return;
	hasRun = true;

	const list = document.querySelectorAll('.list-box');
	const isSingle = list.length === 1;

	if (isSingle) {
		const createResult = createResultBox('.question-box');
		let lis = [...list[0].children];
		lis.forEach(e => {
			searchAnswer(e, createResult);
		});
	} else {
		const createResult = createResultBox('.cmp-testing-onepage-question');
		list.forEach((e) => {
			searchAnswer(e, createResult)
		});
	}
});

function searchAnswer(e, createResult) {
	let wrap;

	if (e.classList.contains('com-danxuan') || e.classList.contains('com-duoxuan') || e.classList.contains('com-panduan')) {
		wrap = e;
	} else {
		wrap = e.querySelector('.com-danxuan') || e.querySelector('.com-duoxuan') || e.querySelector('.com-panduan');
	}

	const questionDOM = wrap.querySelector('h3');
	const question = formatStr(questionDOM.innerText);
	const answer = tk.find(o => o.question === question);
	// const answer = searchInTk(question);
	console.log(questionDOM);
	if (answer) {
		wrap.append(createResult(formatChoices(answer.choices)));
	} else {
		questionDOM.onclick = () => {
			console.log('run there');
			GM_xmlhttpRequest({
				url: `${baseUrl}?token=${token}&question=${question}`,
				method: 'GET',
				timeout: 5000,
				onload: ({ response, status }) => {
					if (status === 200) {
						const res = JSON.parse(response);
						wrap.append(createResult(res.da));
					} else {
						wrap.append(createResult('error'));
					}
				},
				ontimeout: () => {
					wrap.append(createResult('timeout'));
				},
				onerror: () => {
					wrap.append(createResult('error'));
				}
			});
		}
	}
}

function createResultBox(selector) {
	let domArr = [];

	return function (text) {
		return domArr[domArr.push((function () {
			const resultBox = document.createElement('div');
			resultBox.innerText = text;
			resultBox.style.marginTop = 2 + 'rem';
			resultBox.style.color = 'rgb(230 230 230)';

			const wrap = document.querySelector(selector);

			window.onload = function () {
				wrap.onclick = (e) => {
					const target = e.target;
					if (domArr.includes(target)) {
						const cur = target.style.opacity;
						target.style.opacity = cur === '1' ? '0' : '1';
					}
				};
			}

			return resultBox;
		}).apply(this)) - 1];
	}
}

function showAlert(info) {
	const alert = createElementFromHTML(`<div role="alert" class="el-notification" style="bottom: 24px;z-index: 2025;left: 50%;transform: translateX(-50%);"><div class="el-notification__group"><div class="el-notification__content">${info}</div></div></div>`);
	document.querySelector('#app').append(alert);
	setTimeout(() => {
		alert.remove();
	}, 2000);
}

function createElementFromHTML(htmlString) {
	var div = document.createElement('div');
	div.innerHTML = htmlString.trim();

	// Change this to div.childNodes to support multiple top-level nodes
	return div.firstChild;
}

function formatStr(str) {
	const regex = /\p{Unified_Ideograph}*/ug;
	let m;
	let res = '';

	while ((m = regex.exec(str)) !== null) {
		// This is necessary to avoid infinite loops with zero-width matches
		if (m.index === regex.lastIndex) {
			regex.lastIndex++;
		}

		// The result can be accessed through the `m`-variable.
		m.forEach((match, groupIndex) => {
			// console.log(`Found match, group ${groupIndex}: ${match.length}`);
			if (match.replace(' ', '').length) res += match;
		});
	}

	return res;
}

function formatChoices(str) {
	const regex = /(?<=\|\+).*?(?=\||$)/g;
	let m;
	let arr = [];

	while ((m = regex.exec(str)) !== null) {
		// This is necessary to avoid infinite loops with zero-width matches
		if (m.index === regex.lastIndex) {
			regex.lastIndex++;
		}

		// The result can be accessed through the `m`-variable.
		m.forEach((match, groupIndex) => {
			arr.push(match);
		});
	}

	return arr.join('\n');
}

function waitForKeyElements(selectorOrFunction, callback, waitOnce, interval, maxIntervals) {
	if (typeof waitOnce === "undefined") {
		waitOnce = true;
	}
	if (typeof interval === "undefined") {
		interval = 300;
	}
	if (typeof maxIntervals === "undefined") {
		maxIntervals = -1;
	}
	var targetNodes = (typeof selectorOrFunction === "function")
		? selectorOrFunction()
		: document.querySelectorAll(selectorOrFunction);

	var targetsFound = targetNodes && targetNodes.length > 0;
	if (targetsFound) {
		targetNodes.forEach(function (targetNode) {
			var attrAlreadyFound = "data-userscript-alreadyFound";
			var alreadyFound = targetNode.getAttribute(attrAlreadyFound) || false;
			if (!alreadyFound) {
				var cancelFound = callback(targetNode);
				if (cancelFound) {
					targetsFound = false;
				}
				else {
					targetNode.setAttribute(attrAlreadyFound, true);
				}
			}
		});
	}

	if (maxIntervals !== 0 && !(targetsFound && waitOnce)) {
		maxIntervals -= 1;
		setTimeout(function () {
			waitForKeyElements(selectorOrFunction, callback, waitOnce, interval, maxIntervals);
		}, interval);
	}
}