// ==UserScript==
// @name         bilibili快捷键
// @namespace    http://tampermonkey.net/
// @version      0.2
// @author       开花了啦
// @match        *://*.bilibili.com/*
// @icon         https://www.google.com/s2/favicons?domain=bilibili.com
// @description  a/d前进后退视频，s暂停或播放，q/e音量大小改变，t宽屏，c开关弹幕，
// @grant        none
// @downloadURL https://update.greasyfork.org/scripts/459577/bilibili%E5%BF%AB%E6%8D%B7%E9%94%AE.user.js
// @updateURL https://update.greasyfork.org/scripts/459577/bilibili%E5%BF%AB%E6%8D%B7%E9%94%AE.meta.js
// ==/UserScript==


window.addEventListener("keydown", function(e) { //(用keydown因为要连续发)

    if ( e.keyCode == 83 || (e.keyCode == 16 && e.location == 2) ){                //s发暂停或播放  or shift
        if (e.target.querySelector('video')){
            if (!(e.target instanceof HTMLInputElement)){
                e.preventDefault();
                e.stopPropagation();
                e.stopImmediatePropagation();
                stop_video();
            }
        }
    }
    else if ( e.keyCode == 65 ){                //a
        if (e.target.querySelector('video')){
            if (!(e.target instanceof HTMLInputElement)){
                e.preventDefault();
                e.stopPropagation();
                e.stopImmediatePropagation();
                backward_video();
            }
        }
    }
    else if ( e.keyCode == 68 ){                //d
        if (e.target.querySelector('video')){
            if (!(e.target instanceof HTMLInputElement)){
                e.preventDefault();
                e.stopPropagation();
                e.stopImmediatePropagation();
                forward_video();
            }
        }
    }


    else if ( e.keyCode == 67 ){    //c 开关弹幕
        if (!(e.target instanceof HTMLInputElement)){
            if (e.target.querySelector('video')){
                e.preventDefault();
                e.stopPropagation();
                e.stopImmediatePropagation();
                document.querySelector(".bui-danmaku-switch-input").click();
                beep();
            }
        }
    }

    else if ( e.keyCode == 84 ){                        //t 宽屏
        if (!(e.target instanceof HTMLInputElement)){
            if (e.target.querySelector('video')){
                e.preventDefault();
                e.stopPropagation();
                e.stopImmediatePropagation();
                document.querySelector(".bpx-player-ctrl-btn.bpx-player-ctrl-wide").click();
                beep();
            }
        }
    }

    else if ( e.keyCode == 81 ){  //q音量低
        event.preventDefault();
        event.stopPropagation();
        event.stopImmediatePropagation();
        vol_down();
        beep();
    }
    else if ( e.keyCode == 69 ){   //e音量高
        event.preventDefault();
        event.stopPropagation();
        event.stopImmediatePropagation();
        vol_up()
        beep();
    };
});

function vol_up(){
    var vi = document.querySelector("video")
    var vit = vi.volume;
    vi.volume=vit+0.15;
    if(vit+0.15 > 1){
        vi.volume=1;
    }
}

function vol_down(){
    var vi = document.querySelector("video")
    var vit = vi.volume;
    vi.volume=vit-0.15;
    if(vit+0.15 < 0){
        vi.volume=0;
    }
}


function stop_video(){

    var vi = document.querySelector("video")
    if(vi.paused){
        vi.play();
    }else{
        vi.pause();
    }

    blinking(vi);
}

function forward_video(){

    var vi = document.querySelector("video")
    var vit=vi.currentTime;
    vi.currentTime=vit+5;

    blinking(vi);
}

function backward_video(){

    var vi = document.querySelector("video")
    var vit=vi.currentTime;
    vi.currentTime=vit-5;

    blinking(vi);
}

function blinking(vi){
    var timer = setTimeout(function(){
        vi.style.opacity=0.9
    }, 10);
    var timer2 = setTimeout(function(){
        vi.style.opacity=1
    }, 100);
}

//data64 音效接口
function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}