// @updateURL https://update.greasyfork.org/scripts/461645/TLS%E6%B3%95%E7%AD%BE%E5%8A%A9%E6%89%8B%28%E4%B8%93%E6%9D%80%E9%BB%84%E7%89%9B%29.meta.js
// ==/UserScript==

(function() {
    'use strict';
 
    const sound = ("data:audio/mp3;base64,");
    const audioElement = document.createElement('audio');
    audioElement.src = sound;
    audioElement.id = 'audio_III'
    audioElement.autoplay = false;
    audioElement.mute = true;
    document.body.appendChild(audioElement);
 
    function play() {
        audioElement.mute = false;
        try {
            const ele = document.getElementById('audio_III');
            ele.play();
        } catch(e) {
            console.log('failed to play sound');
        }
    }
    function sleep(ms) {
        return new Promise(resolve => setTimeout(resolve, ms));
    }
    sleep(1000);
    const form = document.getElementsByClassName("form_status");
    const normal = document.getElementById('normal_button');
    if (normal) {
        normal.click();
    }
    if (form && form.length > 0) {
        GM_addStyle('a.appt-table-btn.dispo {background-color: #42a00f; color: white}');
        GM_addStyle('a.appt-table-btn.full {background-color: red;color: white}');
        const slot = document.getElementsByClassName("appt-table-btn dispo");
        if (slot && slot.length > 0){
            play();
            let theSlot;
            let slotDate = '';
            let slotType = '';
            try {
                if (slot[0]) {
                    const parent1 = slot[0].closest('.inner_timeslot');
                    if (parent1) {
                        slotDate = parent1.getElementsByClassName('appt-table-d')[0].innerText
                    }
                }
            } catch(e) {
                console.log(e, '---get date error')
                slotDate = '';
            }
            try {
                if (slot[0]) {
                    const parent2 = slot[0].closest('.card');
                    if (parent2) {
                        slotType = parent2.querySelector('button').innerText;
                    }
                }
            } catch(e) {
                console.log(e, '---get type error')
                slotType = '';
            }
            if (slot[0]) {
                slot[0].scrollIntoView({ behavior: 'smooth'});
            }
            window.alert('Available Slot on ' + slotDate + '\n ' + slotType);
            const premiere = document.getElementById('pta_button');
            if (premiere) {
                premiere.click();
            }
        } else{
            setTimeout(function() {
               location.reload();
            }, Math.floor(Math.random() * 20000) + 15000);
        }
        GM_addStyle('.collapse:not(.show) {display: block !important;}');
        GM_addStyle('.container {max-width: fit-content !important;}');
        GM_addStyle('.card.overlapping-lg-card {max-width: 1000px; max-height: 500px}');
        GM_addStyle('div.take_appointment,div.card {height: 1300px !important; width: 3000px !important;}');
        GM_addStyle('.inner_timeslot {max-width: 70px !important;min-width: 70px !important;}');
    }
})();