function saveCustomText() {

	const titleInput = $("#customTextTitle")[0];
	const textInput = $("#customTextText")[0];
	const statusSelect = $("#customTextStatus")[0];
	const signatureCheckbox = $("#customTextSignature")[0];

	const title = titleInput.value;
	const text = textInput.value;
	const statusIndex = statusSelect.selectedIndex;
	const signature = signatureCheckbox.checked;

	let status;

	switch (statusIndex) {
		case 0:
		default:
			status = "open";
			break;
		case 1:
			status = "closed";
			break;
		case 2:
			status = "notidentified";
			break;
	}

	if (!title || !text || !status) {
		alert(URC_USER_PROMPT[18]);
		return;
	}

	if (TextEditIndex !== undefined && TextEditIndex !== -1) {

		// Update text
		DB.transaction((tx) => {
			tx.executeSql("UPDATE `custom_comments_text` SET name = ?, text = ?, status = ?, signature = ? WHERE list = ? AND sorting = ?;", [
				title,
				text,
				status,
				signature,
				options.CustomTextList,
				TextEditIndex,
			], (tx, result) => {

				CustomListText[TextEditIndex] = {
					sorting: CustomListText.length,
					name: title,
					text: text,
					status: status,
					signature: signature
				};

				ShowTextEditFields = false;
				TextEditIndex = undefined;
				refreshUI();

			}, (tx, error) => {
				console.log(error);
				alert(URC_USER_PROMPT[19]);
			});
		});

	} else {

		// Save as new
		DB.transaction((tx) => {
			tx.executeSql("INSERT INTO `custom_comments_text` (list, sorting, name, text, status, signature) VALUES (?, ?, ?, ?, ?, ?);", [
				options.CustomTextList,
				CustomListText.length,
				title,
				text,
				status,
				signature
			], (tx, result) => {
				CustomListText.push({
					sorting: CustomListText.length,
					name: title,
					text: text,
					status: status,
					signature: signature
				});

				ShowTextEditFields = false;
				refreshUI();

			}, (tx, error) => {
				console.log(error);
				alert(URC_USER_PROMPT[19]);
			});
		});

	}
}

