    // 请求加密数据
    function pn(e, {key: n, iv: t}={}) {
        var u = CryptoJS;
        var a = u.enc.Utf8.parse(e)
        , i = u.AES.encrypt(a, u.enc.Utf8.parse(n || dn), {
            iv: u.enc.Utf8.parse(t || un),
            mode: u.mode.CBC,
            padding: u.pad.Pkcs7
        });
        return u.enc.Base64.stringify(i.ciphertext)
    }
