    /*
     * 发送请求，并处理视频地址
    */
    function sendRequire(webSite, requireData, videoDate){
        console.log('正在发生请求');

        if ("undefined" != typeof GM_xmlhttpRequest && typeof(eval('GM_xmlhttpRequest')) == "function"){
            return requireGM(webSite, requireData, videoDate)
        }else{
            return requireFetch(webSite, requireData, videoDate)
        }

    }

    function requireGM(webSite, requireData, videoDate){
        console.log('========================= 执行 Tampermonkey 的GM_xmlhttpRequest函数')
        return new Promise((resolve, reject) =>{
            GM_xmlhttpRequest({
                method: requireData.method, //请求方法 GET POST
                headers: requireData.headers ,
                data: JSON.stringify(requireData.data), //通过 POST 请求发送的字符串
                timeout: 10000, //超时（毫秒）
                responseType: requireData.responseType, // 响应的数据类型 text arraybuffer blob document json ！需要根据返回结果给定类型，不然会请求失败
                overrideMimeType: "text/xml", //请求的 MIME 类型
                url: requireData.url,

                onabort: function () {
                    //如果请求中止，则要执行的回调
                },
                onerror: function (e) {
                    //如果请求最终出现错误，则要执行的回调
                    console.log("请求最终出现错误,将重新请求", e);
                    ++requestErrorTimes;
                    if (requestErrorTimes >5){
                        resolve(requireFetch(webSite, requireData, videoDate))
                    }else{
                        resolve(requireGM(webSite, requireData, videoDate));
                    }
                },
                onloadstart: function () {
                    //在加载开始时执行的回调，如果 responseType 设置为"stream"，则提供对流对象的访问
                },
                onprogress: function () {
                    //如果请求取得了一些进展，则要执行的回调
                },
                onreadystatechange: function () {
                    //在请求的就绪状态发生更改时要执行的回调
                },
                ontimeout: function () {
                    //如果请求由于超时而失败，则要执行的回调
                    console.log("请求由于超时而失败，将重新请求");
                    ++requestErrorTimes;
                    if (requestErrorTimes >5){
                        resolve(requireFetch(webSite, requireData, videoDate))
                    }else{
                        resolve(requireGM(webSite, requireData, videoDate));
                    }
                },
                onload: function (xhr,status) {
                    try{
                        //如果加载了请求，则要执行的回调

                        console.log("请求状态",xhr.status);
                        console.log("请求返回值",xhr.response|| xhr.responseText);

                        // 处理视频播放URL
                        if (webSite == analyzeVideoDict.avPorn.baseUrl){
                            videoDate = dealVideoAddress(webSite, xhr.responseText, videoDate)
                        }else if(webSite == analyzeVideoDict.hxc.baseUrl && xhr.response.code == 0){
                            videoDate = dealVideoAddress(webSite, xhr.response, videoDate)
                        }else {
                            throw new Error(`请求返回code：${xhr.response.code}，返回信息：${JSON.stringify(xhr.response)}`)
                        }

                        // 返回处理后的视频播放地址
                        resolve(videoDate);
                    }catch(erro){
                        erro.message = '发送请求异常！' + erro.message
                        reject(new Error(erro));
                    }
                }
            });
        });
    }

    async function requireFetch(webSite, requireData, videoDate){
        console.log('========================= 执行 fetch 函数')
        try{
            // 请求路径
            let requireUrl = requireData.url;
            // 请求参数
            let initParam = {};

            if(webSite == analyzeVideoDict.hxc.baseUrl){
                let str = '?';
                for (var index in requireData.data) str = str + index + "=" + requireData.data[index] + "&";
                requireUrl = requireUrl + str.substring(0, str.length - 1)
            }
            // 根据请求类型添加参数
            if(requireData.method == 'GET'){
                initParam = {
                    method: requireData.method,
                    headers: new Headers(requireData.headers)
                }
            }
            if(requireData.method == 'POST'){
                initParam = {
                    method: requireData.method,
                    headers: new Headers(requireData.headers),
                    body: JSON.stringify(requireData.data)
                }
            }
            // 返回的结果集
            let result = await fetch(requireUrl,initParam).then(response =>{
                //console.log("请求返回结果",response);
                if (response.ok) {
                    console.log("请求状态",response.status);
                    return requireData.response(response)
                    //if(webSite == analyzeVideoDict.hxc.baseUrl){return response.json()}
                    //if(webSite == analyzeVideoDict.avPorn.baseUrl){return response.text()}
                }else{
                    ++requestErrorTimes;
                    if (requestErrorTimes >10){
                        return Promise.reject(new Error('请求最终出现错误'));
                    }
                    return Promise.resolve(sendRequire(webSite, requireData, videoDate))
                }
            }).catch(error => console.log('错误信息请求',error));
            console.log("请求返回值",result);

            return dealVideoAddress(webSite, result, videoDate)
        }catch(error){
            error.message = 'requireFetch函数错误：'+ error.message
            console.log(error)
            throw new Error(error)
        }
    }

