function initResultQuesID(data) {
    let count = 0;
    let errorItem = []
    let topicArr = $('div[topic]');
    for (let t = 0; t < topicArr.length; t++) {
        let topicItem = topicArr[t];
        // console.log($(element))
        var id = $(topicItem)[0].getAttribute('topic');
        const obj = data.find(function (obj) {
            return obj.id === id
        });
        let answersArray = obj && $(obj)[0].answers || []
        // console.log(obj)
        let ans = $(topicItem).find('.jqradiowrapper').next()
        if (!ans || ans.length === 0) {
            ans = $(topicItem).find('.jqcheckwrapper').next()
        }
        let flag = true;
        for (let j = 0; j < ans.length; j++) {
            let a = ans[j]
            let thisStr = $(a)[0].innerText
            thisStr = thisStr.replace(String.fromCharCode(160), "")
            if (thisStr && thisStr.indexOf("、")) {
                thisStr = thisStr.substring(thisStr.indexOf("、") + 1)
            }
            if (thisStr.charCodeAt(0) === 32) {
                thisStr = thisStr.substring(1)
            }
            for (let i = 0; i < answersArray.length; i++) {
                let answer = answersArray[i]
                answer = answer.replace(String.fromCharCode(160), "")
                if (thisStr == answer) {
                    $(a).css("background-color", "#d2ffc3");
                    flag = false;
                    //$(a).parent().click()
                    break;
                }
            }
        }
        if (flag) {
            errorItem.push(obj)
        } else {
            count++
        }
        //$(topicItem).find('a').last().click()
    }
    console.log("------------------------------")
    for (let i = 0; i < errorItem.length; i++) {
        errorItem[i] && console.log(errorItem[i])
    }
    console.log('count:' + count)
    alert('修改成功'+count)
    // console.log(items)
}
