// ==UserScript==
// @name         Chain Notifier
// @namespace    http://tampermonkey.net/
// @version      0.1
// @description  Add chain timer notifications to Torn
// @author       Wootty2000 [2344687]
// @match        https://www.torn.com/*
// @icon         https://www.google.com/s2/favicons?sz=64&domain=torn.com
// @grant        none
// @license      AGPL v3
// @downloadURL https://update.greasyfork.org/scripts/479236/Chain%20Notifier.user.js
// @updateURL https://update.greasyfork.org/scripts/479236/Chain%20Notifier.meta.js
// ==/UserScript==
//debugger;

var ctx = null;

var chainTimerSecondsLimit;
var chainTimerAlertIfFlying = true;

(function() {
    'use strict';

    var sidebar = document.getElementById("sidebar");
    var informationContent = sidebar.firstChild.firstChild.firstChild.firstChild.lastChild.firstChild;

    var chainObj = informationContent.children[4].children[4];
    var chainTimer = chainObj.children[0].children[2].firstChild;

    if (checkIfChainActive(chainObj))
    {
        loadSettings();

        let newChainTimerP = addNewChainTimerDiv(sidebar);

        var observer = new MutationObserver(function(mutations) {
            mutations.forEach(function(mutation) {
                updateChainTimer(observer, newChainTimerP, chainObj, chainTimer)
            });
        });

        var config = {
            childList: true,
            subtree: true,
            characterData: true
        };

        observer.observe(chainObj, config);
    }
})();

function loadSettings()
{
    chainTimerSecondsLimit = 150;
}

function checkIfChainActive(chainObj)
{
    //No chain, the text is 0/10
    //In cooldown, the text is just 100000, or what ever the hit counter was. So, no "/"
    var chainText = chainObj.children[0].children[1].firstChild;

    if(chainText.data == "0/10" || !chainText.data.includes("/")) {
        return false
    } else {
        return true;
    }
}

function addNewChainTimerDiv(sidebar)
{
    var newChainTimer = document.createElement('div');
    newChainTimer.id = 'chainTimer';
    newChainTimer.style.minHeight = "22px";
    newChainTimer.style.backgroundColor = "#f2f2f2";
    newChainTimer.style.borderRadius = "0 5px 5px 0";

    var newChainTimerP = document.createElement('p');
    newChainTimerP.style.fontSize = "x-large";

    var player = document.createElement('audio');
    player.id = "chainTimerPlayer";

    var source = document.createElement('source');
    source.src = "data:audio/mp3;base64,";
    source.type = "audio/mp3";

    player.appendChild(source);


    /*

    <audio controls="" id="player">
      <source src="data:audio/mp3;base64," type="audio/mp3">
      Your browser does not support the audio element.
    </audio>

    */

    newChainTimer.appendChild(newChainTimerP);
    newChainTimer.appendChild(player);
    sidebar.insertBefore(newChainTimer, sidebar.children[0]);

    return newChainTimerP;
}

function updateChainTimer(observer, newChainTimerP, chainObj, chainTimer)
{
    // If we are no longer chaining, stop checking and remove the chain timer DIV
    if (!checkIfChainActive(chainObj))
    {
        observer.disconnect();
        newChainTimerP.parentElement.remove();
        return;
    }

    newChainTimerP.innerHTML = chainTimer.data;

    var timeArr = chainTimer.data.split(':'); // split it at the colons
    var chainTimerSeconds = ((+timeArr[0]) * 60 + (+timeArr[1]));

    if(chainTimerSeconds < chainTimerSecondsLimit)
    {
        /*
        if(ctx && ctx.state && ctx.state !== "running"){
            ctx.resume();
        }
        else{
            ctx = new AudioContext();
        }
        playSound(400, 0, 0.5, function(){});
        */

        var player = document.getElementById("chainTimerPlayer");
        player.play()

    }
}

function playSound(duration, type, volume, finishedCallback)
{
    duration = +duration;

    // Only 0-3 are valid types.
    type = (type % 4) || 0;

    var types = ['sine', 'square', 'sawtooth', 'triangle'];

    if (typeof finishedCallback != "function") {
        finishedCallback = function () {};
    }

    var osc = ctx.createOscillator();
    var gainNode = ctx.createGain();

    osc.type = types[type];
    gainNode.gain.value = volume || gainNode.gain.defaultValue;
    //osc.type = "sine";

    osc.connect(gainNode);
    gainNode.connect(ctx.destination);
    osc.start();

    setTimeout(function () {
        osc.stop();
        finishedCallback();
    }, duration);
};
