let chatInput = document.querySelector("#chat");

function openChat() {
	let k = window.KeyboardEvent;
	KeyboardEvent = Object;
	window.onkeydown({ isTrusted: true, target: true, code: "Enter", preventDefault: () => {} });
	KeyboardEvent = k;
}

function closeChat() {
	chatInput.dispatchEvent(new KeyboardEvent("keypress", { key: "Enter" }));
}

function say(message) {
	openChat();
	chatInput.value = message;
	closeChat();
}

setInterval(() => {
	say("Auto~Chat~MoD");
}, 2000);

setInterval(() => {
	say("v2.2 - Magic , Let's");
}, 3000);

