	// ==UserScript==
	// @name         otomi-tool
	// @description  ダウンロード済みを管理します。
	// @version      0.4
	// @author       You
	// @match        *://otomi-games.com/*
	// @match        *://kimochi.info/*
	// @match        *://www.dlsite.com/*/work/=/product_id/*.html
	// @icon         data:image/png;base64,
	// @grant        none
	// @license 	 MIT
	// @namespace https://greasyfork.org/users/1286495
// @downloadURL https://update.greasyfork.org/scripts/495782/otomi-tool.user.js
// @updateURL https://update.greasyfork.org/scripts/495782/otomi-tool.meta.js
	// ==/UserScript==

	const localStorageKey = 'page_settings';
	// スキップするIDのリスト
	//基本広告のリストだから、削除する？
	const skipIds = ['post-23678', 'post-20678'];

	//idがtrueかチェックする関数
	function checkForKey(id) {
	    var jsonString = localStorage.getItem(localStorageKey);

	    // JSON文字列をオブジェクトにパースする
	    var settings = JSON.parse(jsonString);

	    // 指定されたキーがオブジェクトに含まれているかをチェックする
	    if (settings.hasOwnProperty(id)) {
	        // 指定されたキーが含まれており、その値がtrueである場合はtrueを返す
	        if (settings[id] === true) {
	            return true;
	        } else {
	            // 指定されたキーが含まれているが、その値がtrueでない場合はfalseを返す
	            return false;
	        }
	    } else {
	        // 指定されたキーが含まれていない場合はfalseを返す
	        return false;
	    }
	}

	//通知を表示する関数
	function showMessage(message) {
	    // メッセージ表示用のdivを作成
	    const displayDiv = document.createElement('div');
	    displayDiv.textContent = message;
	    displayDiv.style.position = 'fixed';
	    displayDiv.style.top = '30px';
	    displayDiv.style.left = 'calc(100% - 250px)'; // 初期位置を右上に設定
	    displayDiv.style.width = '210px'; // 幅を固定
	    displayDiv.style.backgroundColor = 'rgba(255, 255, 255, 0.9)'; // 透明度を追加
	    displayDiv.style.color = '#333'; // 文字色を濃いグレーに変更
	    displayDiv.style.border = '1px solid #ccc'; // ボーダー色を薄いグレーに変更
	    displayDiv.style.borderRadius = '8px'; // ボーダーを丸く
	    displayDiv.style.padding = '10px 20px'; // パディングを調整
	    displayDiv.style.boxShadow = '0 4px 8px rgba(0, 0, 0, 0.1)'; // シャドウを追加
	    displayDiv.style.zIndex = '999999';
	    displayDiv.style.transition = 'opacity 0.5s'; // フェードイン・フェードアウト用のトランジション
	    displayDiv.style.opacity = '0'; // 初期状態は透明にしておく

	    // 閉じるボタンを作成
	    const closeButton = document.createElement('span');
	    closeButton.textContent = '×';
	    closeButton.style.position = 'absolute';
	    closeButton.style.top = '5px';
	    closeButton.style.right = '10px';
	    closeButton.style.cursor = 'pointer';
	    closeButton.style.color = '#333'; // 文字色を濃いグレーに変更
	    closeButton.style.fontSize = '16px';

	    // 閉じるボタンのクリックイベントを設定
	    closeButton.addEventListener('click', () => {
	        displayDiv.style.opacity = '0';
	        setTimeout(() => {
	            displayDiv.remove();
	        }, 500); // フェードアウトの時間に合わせる
	    });

	    // メッセージ表示用のdivに閉じるボタンを追加
	    displayDiv.appendChild(closeButton);

	    // bodyにdivを追加
	    document.body.appendChild(displayDiv);

	    // ドラッグ機能を追加
	    let isDragging = false;
	    let offsetX, offsetY;

	    displayDiv.addEventListener('mousedown', (e) => {
	        isDragging = true;
	        offsetX = e.clientX - displayDiv.getBoundingClientRect().left;
	        offsetY = e.clientY - displayDiv.getBoundingClientRect().top;
	        displayDiv.style.cursor = 'move';
	    });

	    document.addEventListener('mousemove', (e) => {
	        if (isDragging) {
	            displayDiv.style.left = `${e.clientX - offsetX}px`;
	            displayDiv.style.top = `${e.clientY - offsetY}px`;
	        }
	    });

	    document.addEventListener('mouseup', () => {
	        isDragging = false;
	        displayDiv.style.cursor = 'default';
	    });

	    // 短い遅延後にフェードイン
	    setTimeout(() => {
	        displayDiv.style.opacity = '1';
	    }, 10);

	    // 3秒後にフェードアウトして削除
	    setTimeout(() => {
	        displayDiv.style.opacity = '0';
	        setTimeout(() => {
	            displayDiv.remove();
	        }, 500); // フェードアウトの時間に合わせる
	    }, 10000);
	}

	(function() {
	    //otomiのnext
	    document.addEventListener('keydown', function(event) {
	      if (event.shiftKey && event.key === 'N') {

	        var linkElement = document.querySelector('a.page.larger');
	        // 取得した要素からhref属性の値（URL）を取得
	        var url = linkElement.getAttribute('href');
	        window.location.href = url;
	      }
	    });

	    //kimochiのnext
	    document.addEventListener('keydown', function(event) {
	      if (event.shiftKey && event.key === 'N') {

	        var nextPageLink = document.querySelector('a.next.page-numbers');
	        // 取得した要素からhref属性の値（URL）を取得
	        var nextPageUrl = nextPageLink.getAttribute('href');
	        window.location.href = nextPageUrl;
	      }
	    });

	    // localStorageから設定を取得する関数//取得するのは現在のurl(pathname)に対応するもの
	    function getSettings() {
	        var currentUrl = window.location.pathname;
	        var settingsJSON = localStorage.getItem(localStorageKey);
	        if (settingsJSON) {
	            var settings = JSON.parse(settingsJSON);
	            var pageSettings = settings[currentUrl] || {};
	            return pageSettings;
	        } else {
	          // localStorageKey自体が存在しない
	            return false;
	        }
	    }

	    function setSettings(newSettings) {
	        var currentUrl = window.location.pathname;
	        var settingsJSON = localStorage.getItem(localStorageKey);
	        var settings = settingsJSON ? JSON.parse(settingsJSON) : {};
	        settings[currentUrl] = newSettings;
	        localStorage.setItem(localStorageKey, JSON.stringify(settings));
	    }



	    function addNewTab() {
	        // 新しいボタンのHTMLを定義
	        var newButtonHTML = '<button aria-controls="tab-check-content" aria-selected="false" class="ghostkit-tabs-buttons-item check" data-tab="tab-check" id="tab-check-button" role="tab" type="button">check</button>';
	        // 新しいコンテンツのHTMLを定義
	        var contentHTML = `
	    <div class="ghostkit-tab is-layout-flow wp-block-tabs-tab-v2-is-layout-flow" tabindex="0" role="tabpanel" aria-labelledby="tab-check-button" data-tab="tab-check" id="tab-check-content">
	      <p class="has-text-align-center">
	        <input type="checkbox" id="myCheckbox">ダウンロード済み
	    <p>この拡張機能の設定<br></p>
	    <input type="checkbox" id="settingCheckbox">download urlをクリックしたらチェックを付ける

	    </p>
	    </div>
	        `;

	        var tabListButtons = document.querySelector('.ghostkit-tabs-buttons');
	        // コンテンツを追加する要素を取得
	        var tabListContents = document.querySelector('.ghostkit-tabs-content');
	        // 新しいボタンを追加
	        tabListButtons.insertAdjacentHTML('beforeend', newButtonHTML);
	        // 新しいコンテンツを追加
	        tabListContents.insertAdjacentHTML('beforeend', contentHTML);
	    }


	    // Load checkbox state from localStorage
	    function loadCheckboxState() {
	        const checkbox = document.getElementById('myCheckbox');
	        const savedState = getSettings();
	        if (savedState !== null) {
	           checkbox.checked = savedState === true;
	           //タブの名前も変える
	           changeChecktab(checkbox.checked);

	           if (checkbox.checked) {
	           //trueなので通知する
	           showMessage('ダウンロード済みです');
	           }
	        }
	    }

	    // チェックボックスの状態を localStorage に保存する
	    function saveCheckboxState() {
	        const checkbox = document.getElementById('myCheckbox');
	        setSettings(checkbox.checked);
	      //タブの名前も変える
	      changeChecktab(checkbox.checked);
	    }

	    function changeChecktab(bool) {
	        var button = document.getElementById("tab-check-button");
	        if (bool) {
	            button.innerHTML = "<h1>check<sup>true</sup></h1>";

	        } else {
	            button.innerHTML = "<h1>check<sup>false</sup></h1>";
	        }
	    }



	    function addlinkonclickevent() {
	        // リンク要素を取得
	        var links = document.querySelectorAll('p.has-text-align-center a');

	        // 全てのリンクにonclickとonmousedown属性を追加
	        links.forEach(function(link) {

	            link.onclick = function() {
	                setSettings(true);
	                const checkbox = document.getElementById('myCheckbox');
	                checkbox.checked = true;
	                changeChecktab(checkbox.checked);
	            };

	            link.onmousedown = function() {
	                setSettings(true);
	                const checkbox = document.getElementById('myCheckbox');
	                checkbox.checked = true;
	                changeChecktab(checkbox.checked);
	            };

	        });
	    }


	    function addDLMark() {
	      // クラス名が 'clean-grid-grid-post' の全ての要素を取得
	      const elements = document.querySelectorAll('.clean-grid-grid-post');

	      // 要素を配列として扱う場合
	      elements.forEach(element => {
	        // element.idがスキップするIDのリストに含まれる場合はスキップ
	        if (skipIds.includes(element.id)) {
	　　　　　//広告だから消す。
	　　　　　element.remove();
	          return; // 次のelementに進む
	        }
	        // .clean-grid-grid-post-read-moreの中の<a>要素を探す
	        var readMoreLink = element.querySelector('.clean-grid-grid-post-read-more a');

	        // href属性のURLを取得
	        var url = readMoreLink.getAttribute('href');

	        // location.originを空文字列に置き換えることで取り除く
	        var id = url.replace(location.origin, '');

	        //ダウンロード済みの要素はtrue
	        if (checkForKey(id)) {
	          console.log(id);

	          //ダウンロード済みを表示するスタイルを作成
	          const downloaded = document.createElement("div");
	          downloaded.textContent = "ダウンロード済み";
	          downloaded.style.position = "absolute";
	          downloaded.style.top = "20%";
	          downloaded.style.left = "50%";
	          downloaded.style.transform = "translate(-50%, -50%)";
	          downloaded.style.fontSize = "24px";
	          downloaded.style.color = "rgba(0, 128, 0, 0.9)"; // 透明度
	          downloaded.style.pointerEvents = "none"; // クリック判定を無効にする
	          downloaded.style.whiteSpace = "nowrap"; // テキストの折り返しを防ぐ
	          //ダウンロードズ済みのクラスを追加
	          element.appendChild(downloaded);
	　　　　　// 要素そのものに緑のふちを追加
	          element.style.border = '2px solid green';
	        }

	      });
	      console.log("処理終わり");
	    }

	    // dlsiteでidを取得する関数
		function extractProductId() {
		    const pattern = /product_id\/([A-Za-z0-9]+)/;
		    const match = window.location.href.match(pattern);
		    if (match && match[1]) {
		        return match[1];
		    } else {
		        return null;
		    }
		}

		function enablecopydlsite() {
		    var listItem = document.querySelector(".topicpath > .topicpath_item:last-child");
		    if(listItem) {
		        listItem.style.userSelect = "text";
		    }
		    var titleItem = document.querySelector("#work_name");
		    if(titleItem) {
		        titleItem.style.userSelect = "text";
		    }
		}



		if (window.location.host === "www.dlsite.com" || window.location.host === "dlsite.com") {

			// コピーできるようにする関数を実行
			enablecopydlsite();

		    // h1要素のテキストを取得
		    const h1Element = document.getElementById("work_name");
		    // h1要素のテキストを取得し、前後の空白を削除する
		    const title = h1Element.innerText.trim();

		    // Project idを取得する関数を実行
		    const productId = extractProductId();

		    // 新しいテキストを生成
		    const newText = `${title} - product_id: ${productId}`;

		    // テキストを更新する
		    h1Element.innerText = newText;
		}

	    (function() {
	        'use strict';

	        try { addDLMark(); } catch (err) {}
	        try { addNewTab(); } catch (err) {}
	        try { loadCheckboxState(); } catch (err) {}
	        try { addlinkonclickevent(); } catch (err) {}
	        try { document.getElementById('myCheckbox').addEventListener('change', saveCheckboxState); } catch (err) {}
	    })();

	})();