// ==UserScript==
// @name         Habr.com Dark Mode & Settings Changer
// @namespace    https://violentmonkey.github.io/
// @version      1.0
// @description  Change the default theme to Dark, content language and other habr.com page settings
// @author       Streampunk
// @icon         data:image/png;base64,
// @match        https://habr.com/*
// @run-at       document-start
// @grant        none
// @license      MIT
// @downloadURL https://update.greasyfork.org/scripts/501206/Habrcom%20Dark%20Mode%20%20Settings%20Changer.user.js
// @updateURL https://update.greasyfork.org/scripts/501206/Habrcom%20Dark%20Mode%20%20Settings%20Changer.meta.js
// ==/UserScript==

// Set a preference of your choice among the available options. Settings will be saved in browser cookies.
// Theme: dark, light
   var theme = 'dark';

// Interface language: en, ru
   var fl = 'en';

// Content language: en, ru
   var hl = 'en';

// Articles view: cards (classic view), list (compact view)
   var habr_article_view = 'cards';

// Set the value of custom settings as activated, so that the script works properly
   var user_settings = 'activated';

// A Function to Set a Cookie
function setCookie(cName, cValue) {
  const domain = "domain=" + window.location.hostname;
  document.cookie = cName + "=" + cValue + ";" + domain + ";";
}

// A Function to Get a Cookie
function getCookie(cName) {
  let Name = cName + "=";
  let ca = document.cookie.split(';');
  for(let i = 0; i < ca.length; i++) {
    let c = ca[i];
    while (c.charAt(0) == ' ') {
      c = c.substring(1);
    }
    if (c.indexOf(Name) == 0) {
      return c.substring(Name.length, c.length);
    }
  }
  return "";
}

// A Function that Checks if a Cookie is set
function checkCookie() {
  let user = getCookie("user_settings");
  if (user != "") {
 // Remember to open the console (Press F12)
    console.error("Сookies with custom user settings are set!");
  } else {
 // Apply setCookie
    setCookie('theme', theme);
    setCookie('fl', fl);
    setCookie('hl', hl);
    setCookie('habr_article_view', habr_article_view);
    setCookie('user_settings', user_settings);
    location.reload();
  }
}

// Check if Сookies are set and if not, set a Сookie with custom user settings
checkCookie();