// ==UserScript==
// @name         同步转发工具批量功能
// @description  批量添加转发平台、绑定设备、删除设备
// @namespace    http://127.0.0.1:8868
// @version      0.3
// @license      End-User License Agreement
// @author       Liu Peng
// @match        http://127.0.0.1:8868/config
// @icon         data:image/png;base64,
// @require      https://registry.npmmirror.com/jquery/3.7.0/files/dist/jquery.min.js
// @require      https://registry.npmmirror.com/sweetalert2/10.16.6/files/dist/sweetalert2.all.min.js
// @grant        GM_xmlhttpRequest
// @downloadURL https://update.greasyfork.org/scripts/501641/%E5%90%8C%E6%AD%A5%E8%BD%AC%E5%8F%91%E5%B7%A5%E5%85%B7%E6%89%B9%E9%87%8F%E5%8A%9F%E8%83%BD.user.js
// @updateURL https://update.greasyfork.org/scripts/501641/%E5%90%8C%E6%AD%A5%E8%BD%AC%E5%8F%91%E5%B7%A5%E5%85%B7%E6%89%B9%E9%87%8F%E5%8A%9F%E8%83%BD.meta.js
// ==/UserScript==

$(function () {
    // 请求头
    const headers = {
        'Accept': 'text/plain, */*; q=0.01',
        'Accept-Language': 'zh-CN,zh;q=0.9',
        'Connection': 'keep-alive',
        'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
        'Origin': 'http://127.0.0.1:8868',
        'Referer': 'http://127.0.0.1:8868/config',
        'Sec-Fetch-Dest': 'empty',
        'Sec-Fetch-Mode': 'cors',
        'Sec-Fetch-Site': 'same-origin',
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/126.0.0.0 Safari/537.36',
        'X-Requested-With': 'XMLHttpRequest',
        'sec-ch-ua': '"Not/A)Brand";v="8", "Chromium";v="126", "Google Chrome";v="126"',
        'sec-ch-ua-mobile': '?0',
        'sec-ch-ua-platform': '"Windows"'
    }

    let toast = Swal.mixin({
        allowOutsideClick: false,
        confirmButtonText: '确认',
        didOpen: () => {
            Swal.hideLoading()
        }
    });

    const message = {
        success: (text) => {
            toast.fire({title: text, icon: 'success'});
        },
        error: (text) => {
            toast.fire({title: text, icon: 'error'});
        },
        warning: (text) => {
            toast.fire({title: text, icon: 'warning'});
        },
        info: (text) => {
            toast.fire({title: text, icon: 'info'});
        },
        question: (text) => {
            toast.fire({title: text, icon: 'question'});
        }
    }

    addDiv()
    addButton('batch_add_transmit', '批量添加转发', addTransmitSwal)
    addButton('batch_add_devices', '批量添加设备', addDeviceSwal)
    addButton('batch_delete_devices', '批量删除设备', deleteDeviceSwal)

    function addDiv() {
        let div = document.createElement('div')
        div.id = 'self_build_button_group';
        div.style.zIndex = '100000';
        div.style.position = 'fixed';
        div.style.top = 0;
        div.style.right = 0;
        document.body.appendChild(div);
    }

    /**
     * 添加按钮
     */
    function addButton(id, innerText, event) {
        let button = document.createElement('button');
        button.id = id;
        button.innerText = innerText;
        button.style.marginRight = '10px'
        button.style.marginTop = '15px'
        button.addEventListener("click", event);
        document.getElementById('self_build_button_group').appendChild(button);
    }

    /**
     * 添加转发弹出框
     * @returns {Promise<void>}
     */
    async function addTransmitSwal() {
        let failureData = []
        let dataSize = {
            task: 0,
            processed: 0,
            inAll: 0
        }

        async function addTransmit(transmitInfo) {
            const result = await request.saveSkInfo(transmitInfo)
            if (!result.r) {
                failureData.push({'skmc': transmitInfo.skmc, 'msg': result.msg})
            }
        }

        async function parseAddTransmitInputValue(inputValue) {
            const transmitList = inputValue.split('\n')
            dataSize.inAll = transmitList.length
            const transmitInfoList = []
            for (const transmit of transmitList) {
                const parseTransmit = transmit.split('\t')
                if (parseTransmit.length !== 4) {
                    dataSize.processed += 1
                    failureData.push({'transmitName': parseTransmit[0], 'msg': '格式错误'})
                    continue
                }
                dataSize.task = parseTransmit[0]
                await addTransmit(parseTransmit)
                dataSize.processed += 1
            }
            if (failureData.length !== 0) {
                console.log(failureData)
                return message.error("批量添加转发未全部成功，请在控制台中查看添加失败的转发")
            } else {
                return message.success("批量添加转发成功")
            }
        }

        await Swal.fire({
            title: "批量添加转发平台",
            input: "textarea",
            inputPlaceholder: "格式为'转发名称\tclientId\tclientKey\tMas地址'，多个转发平台以换行符分隔",
            allowOutsideClick: false,
            preConfirm: (inputValue) => {
                if (inputValue === '') {
                    Swal.showValidationMessage(`请输入转发系信息`);
                    return
                }
                let timerInterval
                Swal.fire({
                    title: '正在绑定，请稍候!',
                    allowOutsideClick: false,
                    html: "当前任务：<b></b><br>已处理：<b></b> / <b></b>",
                    didOpen: async () => {
                        Swal.showLoading()
                        const b0 = Swal.getPopup().querySelectorAll("b")[0]
                        const b1 = Swal.getPopup().querySelectorAll("b")[1]
                        const b2 = Swal.getPopup().querySelectorAll("b")[2]
                        timerInterval = setInterval(() => {
                            b0.textContent = dataSize.task
                            b1.textContent = dataSize.processed
                            b2.textContent = dataSize.inAll
                        }, 100)
                    },
                    didDestroy: () => {
                        clearInterval(timerInterval)
                    }
                })
                parseAddTransmitInputValue(inputValue)
            },
            showCancelButton: true,
            confirmButtonText: "确定",
            cancelButtonText: "取消"
        });
    }

    /**
     * 添加设备弹出框
     * @returns {Promise<void>}
     */
    async function addDeviceSwal() {
        let dataSize = {
            task: 0,
            processed: 0,
            inAll: 0
        }
        let failureData = []
        let skInfoS = await request.querySkInfos()

        async function addDevice(deviceInfo) {
            // 绑定基站
            const result = await request.saveDevice(deviceInfo)
            if (!result.r) {
                failureData.push({'deviceName': deviceInfo[1], 'msg': result.msg})
            }
        }

        async function parseAddDeviceInputValue(skId, inputValue) {
            let deviceList = inputValue.split('\n')
            deviceList = deviceList.filter(e => e !== '')
            dataSize.inAll = deviceList.length
            for (let deviceInfoStr of deviceList) {
                const deviceInfo = deviceInfoStr.split('\t');
                if (deviceInfo.length !== 7) {
                    failureData.push({'source': deviceInfo, 'msg': '格式错误'})
                    dataSize.processed += 1
                    continue
                }
                if (deviceInfo.includes('')) {
                    failureData.push({'source': deviceInfo, 'msg': '参数包含空值'})
                    dataSize.processed += 1
                    continue
                }
                dataSize.task = skInfoS[skId]+" "+deviceInfo[1]
                deviceInfo.push(skId)
                await addDevice(deviceInfo)
                dataSize.processed += 1
            }
            if (failureData.length !== 0) {
                console.log(failureData)
                return message.error("添加设备未全部成功，请在控制台中查看添加失败的设备")
            } else {
                return message.success("添加设备成功")
            }
        }

        await Swal.fire({
            title: "添加设备",
            input: "select",
            inputOptions: skInfoS,
            inputPlaceholder: "请选择转发",
            html: `<textarea id="swal-textarea" class="swal2-input" style="margin-bottom: 0" placeholder="请输入设备信息"></textarea>`,
            allowOutsideClick: false,
            preConfirm: (skId) => {
                const inputValue = document.getElementById("swal-textarea").value
                if (skId === '') {
                    Swal.showValidationMessage(`请选择转发`);
                    return
                }
                if (inputValue === '') {
                    Swal.showValidationMessage(`请输入设备信息`);
                    return
                }
                let timerInterval
                Swal.fire({
                    title: '正在绑定，请稍候!',
                    allowOutsideClick: false,
                    html: "当前任务：<b></b><br>已处理：<b></b> / <b></b>",
                    didOpen: async () => {
                        Swal.showLoading()
                        const b0 = Swal.getPopup().querySelectorAll("b")[0]
                        const b1 = Swal.getPopup().querySelectorAll("b")[1]
                        const b2 = Swal.getPopup().querySelectorAll("b")[2]
                        timerInterval = setInterval(() => {
                            b0.textContent = dataSize.task
                            b1.textContent = dataSize.processed
                            b2.textContent = dataSize.inAll
                        }, 100)
                    },
                    didDestroy: () => {
                        clearInterval(timerInterval)
                    }
                })
                parseAddDeviceInputValue(skId, inputValue)
            },
            showCancelButton: true,
            confirmButtonText: "确定",
            cancelButtonText: "取消"
        });
    }

    /**
     * 删除设备弹出框
     * @returns {Promise<void>}
     */
    async function deleteDeviceSwal() {
        let failureData = []
        let dataSize = {
            task: 0,
            processed: 0,
            inAll: 0
        }

        async function deleteDevice(deviceId) {
            const result = await request.deleteDevice(deviceId)
            if (!result.r) {
                failureData.push({'deviceId': deviceId.skmc, 'msg': result.msg})
            }
        }

        async function parseDeleteDeviceInputValue(inputValue) {
            const deviceIdList = inputValue.split('\n')
            dataSize.inAll = deviceIdList.length
            for (let deviceId of deviceIdList) {
                dataSize.task = deviceId
                await deleteDevice(deviceId)
                dataSize.processed+=1
            }
            if (failureData.length !== 0) {
                console.log(failureData)
                return message.error("批量删除设备未全部成功，请在控制台中查看删除失败的设备")
            } else {
                return message.success("批量删除设备成功")
            }
        }

        await Swal.fire({
            title: "批量删除设备",
            input: "textarea",
            inputPlaceholder: "格式为'id'，多个id以换行符分隔",
            allowOutsideClick: false,
            preConfirm: (inputValue) => {
                if (inputValue === '') {
                    Swal.showValidationMessage(`请输入设备信息`);
                    return
                }
                let timerInterval
                Swal.fire({
                    title: '正在绑定，请稍候!',
                    allowOutsideClick: false,
                    html: "当前任务：<b></b><br>已处理：<b></b> / <b></b>",
                    didOpen: async () => {
                        Swal.showLoading()
                        const b0 = Swal.getPopup().querySelectorAll("b")[0]
                        const b1 = Swal.getPopup().querySelectorAll("b")[1]
                        const b2 = Swal.getPopup().querySelectorAll("b")[2]
                        timerInterval = setInterval(() => {
                            b0.textContent = dataSize.task
                            b1.textContent = dataSize.processed
                            b2.textContent = dataSize.inAll
                        }, 100)
                    },
                    didDestroy: () => {
                        clearInterval(timerInterval)
                    }
                })
                parseDeleteDeviceInputValue(inputValue)
            },
            showCancelButton: true,
            confirmButtonText: "确定",
            cancelButtonText: "取消"
        });
    }

    const request = {
        saveSkInfo: (transmitInfo) => {
            let result = {
                r: true,
                msg: ''
            }
            const data = `id=&skmc=${transmitInfo[0]}&clientId=${transmitInfo[1]}&clientKey=${transmitInfo[2]}&masUrl=${transmitInfo[3]}`
            return new Promise((resolve, reject) => {
                GM_xmlhttpRequest({
                    method: "POST",
                    url: "http://127.0.0.1:8868/saveSkInfo",
                    headers: headers,
                    data:data,
                    onload: function (res) {
                        result.r = res.status === 200;
                        resolve(result)
                    },
                    onerror: function (error) {
                        result.r = false
                        result.msg = error
                        resolve(result)
                    }
                })
            })
        },
        querySkInfos: () => {
            let skInfoS = {}
            return new Promise((resolve, reject) => {
                GM_xmlhttpRequest({
                    method: "GET",
                    url: "http://127.0.0.1:8868/querySkInfos",
                    headers: headers,
                    responseType: 'JSON',
                    onload: function (res) {
                        for (const skInfo of res.response) {
                            skInfoS[skInfo.id] = skInfo.skmc
                        }
                        resolve(skInfoS)
                    },
                    onerror: function (error) {
                        return message.error(error)
                    }
                })
            })
        },
        saveDevice: (deviceInfo) => {
            let result = {
                r: true,
                msg: ''
            }
            const data = `id=&type=${deviceInfo[0]}&deviceName=${deviceInfo[1]}&sensorId=${deviceInfo[2]}&txbm=${deviceInfo[3]}&tdh=1&cygbm=1&csvPath=${deviceInfo[4]}&masDeviceId=${deviceInfo[5]}&masApikey=${deviceInfo[6]}&skId=${deviceInfo[7]}`
            return new Promise((resolve, reject) => {
                GM_xmlhttpRequest({
                    method: "POST",
                    url: "http://127.0.0.1:8868/saveDevice",
                    headers: headers,
                    data:data,
                    onload: function (res) {
                        result.r = res.status === 200;
                        resolve(result)
                    },
                    onerror: function (error) {
                        result.r = false
                        result.msg = error
                        resolve(result)
                    }
                })
            })
        },
        deleteDevice: (id) => {
            let result = {
                r: true,
                msg: ''
            }
            const data = `id=${id}`
            return new Promise((resolve, reject) => {
                GM_xmlhttpRequest({
                    method: "POST",
                    url: "http://127.0.0.1:8868/deleteDevice?" + data,
                    headers: headers,
                    onload: function (res) {
                        result.r = res.status === 200;
                        resolve(result)
                    },
                    onerror: function (error) {
                        result.r = false
                        result.msg = error
                        resolve(result)
                    }
                })
            })
        },
    }
})