// ==UserScript==
// @name         Phigros in Luogu (Improved)
// @namespace    http://tampermonkey.net/
// @version      1.1.6
// @description  Incorporate Phigros difficulty system into Luogu.
// @author       Gapple
// @match        https://www.luogu.com.cn/*
// @grant        none
// @license      MIT
// @downloadURL https://update.greasyfork.org/scripts/515921/Phigros%20in%20Luogu%20%28Improved%29.user.js
// @updateURL https://update.greasyfork.org/scripts/515921/Phigros%20in%20Luogu%20%28Improved%29.meta.js
// ==/UserScript==

(function () {
    'use strict';
    function setName(lv, ratio) {
        if (typeof (ratio) == "string" && ratio[ratio.length - 1] == "%") ratio = ratio.substring(ratio, ratio.length - 1) / 100;
        function calclv(l, r) {
            if (ratio > 1) ratio = 1;
            return Math.floor(l + (r - l) * Math.pow((1 - ratio), 3) + 0.5);
        }
        let s = lv.innerText;
        if (s == "暂无评定" || s == "入门" || s == "普及−" || s == "普及/提高−" || s == "普及+/提高" || s == "提高+/省选−" || s == "省选/NOI−" || s == "NOI/NOI+/CTSC" || s.includes("EZ") || s.includes("HD") || s.includes("IN") || s.includes("AT")) {
            let color = lv.style.background == "" ? lv.style.color : lv.style.background;
            if (color == "rgb(254, 76, 97)") lv.innerText = "EZ" + (ratio == undefined ? "" : " Lv. " + calclv(1, 3));
            if (color == "rgb(243, 156, 17)") lv.innerText = "EZ" + (ratio == undefined ? "" : " Lv. " + calclv(3, 7));
            if (color == "rgb(255, 193, 22)") lv.innerText = "HD" + (ratio == undefined ? "" : " Lv. " + calclv(7, 10));
            if (color == "rgb(82, 196, 26)") lv.innerText = "HD" + (ratio == undefined ? "" : " Lv. " + calclv(9, 12));
            if (color == "rgb(52, 152, 219)") lv.innerText = "IN" + (ratio == undefined ? "" : " Lv. " + calclv(12, 14));
            if (color == "rgb(157, 61, 207)") lv.innerText = "IN" + (ratio == undefined ? "" : " Lv. " + calclv(13, 15));
            if (color == "rgb(14, 29, 105)") lv.innerText = "AT" + (ratio == undefined ? "" : " Lv. " + calclv(15, 16));
            if (color == "rgb(191, 191, 191)") lv.innerText = "SP" + (ratio == undefined ? "" : " Lv. ?");
        }
    }
    setInterval(() => {
        if (location.pathname.includes("/problem/")) {
            if (location.pathname.includes("list")) {
                let tmp = document.getElementsByClassName("row");
                // console.log(tmp);
                for (let i = 0; i < tmp.length; i++) {
                    setName(tmp[i].children[4].children[0].children[0], tmp[i].children[5].children[0].children[0].style.width);
                    tmp[i].style.transform = "translateX(" + -i * 10 + ")";
                }
            } else {
                let tmp = document.getElementsByClassName("stat color-inverse")[0];
                let x = tmp.children[0].children[1].innerText; // 提交
                if (x[x.length - 1] == "k") x = x.substring(0, x.length - 1) * 1000;
                if (x[x.length - 1] == "M") x = x.substring(0, x.length - 1) * 1000000;
                let y = tmp.children[1].children[1].innerText; // 通过
                if (y[y.length - 1] == "k") y = y.substring(0, y.length - 1) * 1000;
                if (y[y.length - 1] == "M") y = y.substring(0, y.length - 1) * 1000000;

                // 定位到题目难度
                let info = document.getElementsByClassName("info-rows")[0];
                let lv = info.children[info.children.length - 2].children[1].children[0].children[0];
                setName(lv, y / x);
                let sc = info.children[info.children.length - 1].children[1].children[0].children[0];
                let fullScore = 0;
                let str = document.evaluate("/html/head/script[1]", document, null, XPathResult.FIRST_ORDERED_NODE_TYPE, null).singleNodeValue.childNodes[0].nodeValue;
                let start = str.match("fullScore%22%3A").index + "fullScore%22%3A".length;

                for (let i = start; i < str.length && !isNaN(Number(str[i])); i++) {
                    fullScore = fullScore * 10 + Number(str[i]);
                }
                if (sc.dataset["phiflag"] != 1) {
                    sc.dataset["phiflag"] = 1;
                    if (!isNaN(Number(sc.innerText))) {
                        sc.innerText *= 10000;
                        if (sc.innerText == fullScore * 10000) {
                            let img = document.createElement("img");
                            img.src = "data:image/png;base64,";
                            img.style.height = "50px";
                            sc.remove();
                            info.children[info.children.length - 1].children[1].children[0].appendChild(img);
                        } else {
                            sc.innerText = (sc.innerText == 0 ? "000000" : (sc.innerText / 10000 < 10 ? "00" : "0")) + sc.innerText;
                        }
                    } else if (sc.innerText == "AC") {
                        let img = document.createElement("img");
                        img.src = "data:image/png;base64,";
                        img.style.height = "50px";
                        sc.remove();
                        info.children[info.children.length - 1].children[1].children[0].appendChild(img);
                    }
                }
            }
        }
        else if (location.pathname.includes("/training/")) {
            if (location.pathname.includes("list")) {

            } else {
                let tmp = document.getElementsByClassName("row");
                for (let i of tmp) setName(i.children[4].children[0].children[0], i.children[5].children[0].children[0].style.width);
            }
        }
        else if ((new RegExp('\\/record\\/\\d+')).test(location.pathname)) {
            let info = document.getElementsByClassName("info-rows")[0];
            let state = info.children[info.children.length - 3].children[1].children[0].innerText;

            if (state.includes("Accepted")) {
                info.children[info.children.length - 3].children[1].children[0].innerText = "Perfect";
            }
            else if (state.includes("Unaccepted")) {
                if (info.children.length >= 4) {
                    let score = info.children[info.children.length - 2].children[1].children[0].children[0].innerText;
                    info.children[info.children.length - 3].children[1].children[0].innerText = score == 0 ? "Miss" : (score >= 60 ? "Good" : "Bad");
                } else {
                    info.children[info.children.length - 3].children[1].children[0].innerText = "Bad";
                }
            }

            if (info.children.length >= 4) {
                if (info.children[info.children.length - 2].children[1].children[0].children[0].innerText <= 100) {
                    info.children[info.children.length - 2].children[1].children[0].children[0].innerText *= 10000;
                }

                if (info.children[info.children.length - 2].children[1].children[0].children[0].innerText < 1000000 && info.children[info.children.length - 2].children[1].children[0].children[0].innerText.length < 7) {
                    info.children[info.children.length - 2].children[1].children[0].children[0].innerText = (info.children[info.children.length - 2].children[1].children[0].children[0].innerText == 0 ? "000000" : (info.children[info.children.length - 2].children[1].children[0].children[0].innerText / 10000 < 10 ? "00" : "0")) + info.children[info.children.length - 2].children[1].children[0].children[0].innerText;
                } else if (state.includes("Accepted") || state.includes("Perfect")) {
                    let img = document.createElement("img");
                    img.src = "data:image/png;base64,";
                    img.style.height = "50px";
                    info.children[info.children.length - 2].children[1].children[0].children[0].remove();
                    info.children[info.children.length - 2].children[1].children[0].appendChild(img);
                }
            }
        }
        else if (location.pathname.includes("/record/")) {
            let tmp = document.getElementsByClassName("lfe-caption tag status-name");
            for (let i of tmp) {
                if (i != i.parentNode.children[1]) {
                    /*
                    制作一个替身，来修改显示的内容，但不影响原来的数据
                    */
                    if (i.dataset["phiflag"] != 1) { // flag：0 没有替身 1：有替身
                        i.dataset["phiflag"] = 1;
                        let s2 = document.createElement("span");
                        s2.className = "lfe-caption tag status-name";
                        s2.style.display = "none";
                        console.log(i.parentNode);
                        i.parentNode.insertBefore(s2, i.parentNode.children[1]);
                    } else {
                        let c = i.parentNode.children[1];
                        c.style.background = i.style.background;
                        c.style.color = "rgb(255, 255, 255)";
                        c.style.fontFamily = i.style.fontFamily;
                        c.style.paddingLeft = "8px";
                        c.style.paddingRight = "8px";
                        if (i.innerText.includes("Accepted")) {
                            c.style.display = "block";
                            c.innerText = "Perfect";
                            i.style.display = "none";
                        } else if (i.innerText.includes("Unaccepted")) {
                            c.style.display = "block";
                            c.innerText = i.parentNode.children[2].innerText == 0 ? "Miss" : (i.parentNode.children[2].innerText >= 60 ? "Good" : "Bad");
                            i.style.display = "none";
                        } else {
                            c.style.display = "none";
                            i.style.display = "block";
                        }
                    }
                }
            }
        }
    }, 10);
})();