function svg2url(svgString) {
    const parser = new DOMParser(),
          doc = parser.parseFromString(svgString, "image/svg+xml"),
          svgEl = doc.documentElement,
          serialized = new XMLSerializer().serializeToString(svgEl),
          encoded = encodeURIComponent(serialized).replace(/'/g, "%27").replace(/"/g, "%22");

    return `data:image/svg+xml;charset=utf-8,${encoded}`;
}

function getHumanTime(key) {
    let timeKey = key.split(':'),
        hourKey = '', minuteKey = '', secondKey = '', amPM = '';

    hourKey = parseInt(timeKey[0]);

    if (hourKey === 24) {
        hourKey = 0;
    }

    if (hourKey >= 12) {
        amPM = 'PM';
        hourKey = hourKey - 12;
    } else if (hourKey <= 11) {
        amPM = 'AM';
    }

    minuteKey = parseInt(timeKey[1]);

    if (hourKey.toString().length === 1) {
        hourKey = `0${hourKey}`;
        if (hourKey === '00') {
            hourKey = '12';
        }
    }
    if (minuteKey.toString().length === 1) {
        minuteKey = `0${minuteKey}`;
    }

    if (timeKey.length > 2) {
        secondKey = parseInt(timeKey[2]);

        if (secondKey.toString().length === 1) {
            secondKey = `0${secondKey}`;
        }

        return `${hourKey}:${minuteKey}:${secondKey} ${amPM}`;
    } else {
        return `${hourKey}:${minuteKey} ${amPM}`;
    }
}

function fixTime(query) {
    let elems = document.querySelectorAll(query);

    for (let x = 0; x < elems.length; x++) {
        if (elems[x].textContent.match(/..:..:.. [^AP]/)) {
            let timeFound = elems[x].innerHTML.replace(/.*(..:..:..).*/, '$1');
            elems[x].innerHTML = elems[x].innerHTML.replace(/..:..:../, getHumanTime(timeFound));
        } else if (elems[x].textContent.match(/^..:..:..$/)) {
            let timeFound = elems[x].textContent.replace(/.*(..:..:..).*/, '$1');
            elems[x].textContent = elems[x].textContent.replace(/..:..:../, getHumanTime(timeFound));
        } else if (elems[x].textContent.match(/^ ..:.. $/)) {
            let timeFound = elems[x].textContent.replace(/.*(..:..).*/, '$1');
            elems[x].textContent = elems[x].textContent.replace(/..:../, getHumanTime(timeFound));
        }
    }
}

function fixDoubleTime(query) {
    let elems = document.querySelectorAll(query);

    for (let x = 0; x < elems.length; x++) {
        if (elems[x].innerHTML.match(/..:..:.. [^AP].*..:..:.. [^AP]/)) {
            let timeFound = elems[x].innerHTML.replace(/.*(..:..:..).*(..:..:..).*/, '$1'),
                timeFound2 = elems[x].innerHTML.replace(/.*(..:..:..).*(..:..:..).*/, '$2');
            elems[x].innerHTML = elems[x].innerHTML.replace(/..:..:..(.*)..:..:../, `${getHumanTime(timeFound).replace(/:..( [AP]M)$/,'$1')}$1${getHumanTime(timeFound2).replace(/:..( [AP]M)$/,'$1')}`);
        }
        if (elems[x].innerHTML.match(/^ ..:..-..:.. $/)) {
            let timeFound = elems[x].innerHTML.replace(/.*(..:..).*(..:..).*/, '$1'),
                timeFound2 = elems[x].innerHTML.replace(/.*(..:..).*(..:..).*/, '$2');
            elems[x].innerHTML = elems[x].innerHTML.replace(/..:..(.*)..:../, `${getHumanTime(timeFound)} $1 ${getHumanTime(timeFound2)}`);
        }
        if (elems[x].innerHTML.match(/ ..:.. - ..:.. /)) {
            let timeFound = elems[x].innerHTML.replace(/.*(..:..).*(..:..).*/, '$1'),
                timeFound2 = elems[x].innerHTML.replace(/.*(..:..).*(..:..).*/, '$2');
            elems[x].innerHTML = elems[x].innerHTML.replace(/..:..(.*)..:../, `${getHumanTime(timeFound)} $1 ${getHumanTime(timeFound2)}`);
        }
    }
}

