function populateOBE() {
    let obe = document.querySelector('#showOBEInfo obe'),
        thead = document.querySelector('table thead > tr').children,
        quizArray = [],
        print = '';

    for (let x = 0; x < thead.length; x++) {
        if (!thead[x].textContent.match(/%/)) {continue;}

        let qName = thead[x].textContent.replace(/(.*)\(.*/,'$1'),
            qScore = thead[x].textContent.replace(/.*\((.*)%.*/,'$1');

        quizArray.push({"name": qName, "num": x, "score": parseInt(qScore)});
    }

    function findClosestNumber(arr, target) {
        if (arr.length === 0) return null;

        let closest = arr[0];
        let minDiff = Math.abs(target - closest);

        for (let i = 1; i < arr.length; i++) {
            const current = arr[i];
            if (current === undefined) {continue};

            const diff = Math.abs(target - current);

            if (diff < minDiff || (diff === minDiff && current < closest)) {
                minDiff = diff;
                closest = current;
            }
        }

        return closest;
    }

    function printQuiz() {
        for (let x = 0; x < quizArray.length; x++) {
            let allInput = document.querySelectorAll('table tbody > tr'),
                inputArray = [],
                infoArray = [];

            for (let y = 0; y < allInput.length; y++) {
                if (!allInput[y].children[allInput[y].children.length - 2].textContent.match(/f/i)) {
                    let tnum = parseFloat(allInput[y].children[quizArray[x].num].querySelector('input').value);

                    if (tnum >= quizArray[x].score * 0.4) {
                        inputArray.push(tnum);
                        infoArray.push({"name": allInput[y].children[1].children[0].children[1].children[0].textContent.toUpperCase(),
                                        "code": allInput[y].children[1].children[0].children[1].children[1].textContent});
                    }
                }
            }

            // alert(inputArray);

            let orderedArray = [...inputArray].sort((a, b) => a - b),
                marginal = orderedArray[0],
                highest = orderedArray[orderedArray.length-1],
                average = findClosestNumber(orderedArray, ((marginal + highest) / 2)),

                marginalName = infoArray[inputArray.indexOf(marginal)].name,
                marginalCode = infoArray[inputArray.indexOf(marginal)].code,
                highestName = infoArray[inputArray.indexOf(highest)].name,
                highestCode = infoArray[inputArray.indexOf(highest)].code,
                averageName = infoArray[inputArray.indexOf(average)].name,
                averageCode = infoArray[inputArray.indexOf(average)].code;

              // alert(JSON.stringify(infoArray));

            print += `<colspan class="obe-${quizArray[x].name.replace(/[\s\t]/g,'').toLowerCase()}">
                          <cellspan class="title">${quizArray[x].name} (${quizArray[x].score}%)</cellspan>
                          <cellspan class="marginal">
                              <span>${marginal}</span>
                              <span>${marginalName}</span>
                              <span>${marginalCode}</span>
                          </cellspan>
                          <cellspan class="highest">
                              <span>${highest}</span>
                              <span>${highestName}</span>
                              <span>${highestCode}</span>
                          </cellspan>
                          <cellspan class="average">
                              <span>${average}</span>
                              <span>${averageName}</span>
                              <span>${averageCode}</span>
                          </cellspan>
                      </colspan>`;
        }

        return print;
    }

    obe.innerHTML = printQuiz();
}

