function autoFillScores(arrayVal) {
    let allInput = document.querySelectorAll('input[id*="input"]');

    for (let x = 0; x < allInput.length; x++) {
        setTimeout(() => {
            allInput[x].value = parseFloat(arrayVal[x].replace(/\-/,''));
            allInput[x].dispatchEvent(new Event('input', { bubbles: true }));
        }, 10 * x);
    }
}

function getUrlVars(link) {
    let vars = {};
    let parts = link.replace(/[?&]+([^=&]+)=([^&]*)/gi, function(m,key,value) {
        vars[key] = value;
    });
    return vars;
}

