    // Roman to Dec and Dec to Roman
    const romanInt = {
        map: [
            'N/A','I','II','III','IV','V','VI','VII','VIII','IX','X','XI','XII','XIII','XIV','XV','XVI','XVII','XVIII','XIX','XX'
        ],
        parse: function (v) {
            if (typeof v === 'number') return romanInt.map[v] || 'N/A';
            const i = romanInt.map.indexOf(String(v).toUpperCase());
            return i === -1 ? 'N/A' : i;
        }
    };

    // +/- question no.
    function setQ(key, t) {
        if (key === '+') {
            if (t.closest('.q-item--question')) {
                let val = t.closest('.q-item--question').querySelector('.q-item-value');

                if (parseInt(val.textContent) + 1 > 20) {
                    val.textContent = 20;
                } else {
                    val.textContent = parseInt(val.textContent) + 1;
                }
            } else if (t.closest('.q-item--subquestion')) {
                let val = t.closest('.q-item--subquestion').querySelector('.q-item-value');

                if (romanInt.parse(val.textContent) + 1 > 20) {
                    val.textContent = romanInt.parse(20);
                } else {
                    val.textContent = romanInt.parse(romanInt.parse(val.textContent) + 1);
                }
            }
        } else if (key === '-') {
            if (t.closest('.q-item--question')) {
                let val = t.closest('.q-item--question').querySelector('.q-item-value');

                if (parseInt(val.textContent) - 1 < 1) {
                    val.textContent = 1;
                } else {
                    val.textContent = parseInt(val.textContent) - 1;
                }
            } else if (t.closest('.q-item--subquestion')) {
                let val = t.closest('.q-item--subquestion').querySelector('.q-item-value');

                if (romanInt.parse(val.textContent) - 1 < 0) {
                    val.textContent = romanInt.parse(0);
                } else {
                    val.textContent = romanInt.parse(romanInt.parse(val.textContent) - 1);
                }
            }
        }
    }

    // Function for +/- new COs
    function setCO(key) {
        let coNum = document.querySelector('#obeBody .co-number'),
            olist = document.querySelector('#obeBody .outcome-list').querySelector('.co-item.delete-not'),
            coRow = document.querySelector('#obeBody .copo-mapping .copo-row:last-of-type'),
            qSrc = document.querySelector('#obeBody .assess-alignment .q-source .q-list'),
            qItm = document.querySelector('#obeBody .assess-alignment .q-source .q-item');

        new Sortable(qSrc, {
            group: {
                name: 'shared',
                pull: 'clone',
                put: false
            },
            filter: '.q-label',
            animation: 150,
            sort: false,
            onEnd: function () {
                updateQNo();
                quickSum();
            }
        });

        new Sortable(document.querySelector('.q-trash'), {
            group: {
                name: 'shared',
                pull: false,
                put: true
            },
            filter: '.q-label',
            animation: 150,
            sort: false,
            onEnd: function () {
                updateQNo();
                quickSum();
            }
        });

        for (let x = 0; x < window.examArray.length; x++) {
            new Sortable(document.querySelector(window.examArray[x] + ' .q-list'), {
                group: {
                    name: 'shared',
                    pull: true,
                    put: true
                },
                filter: '.q-label',
                animation: 150,
                sort: true,
                onEnd: function () {
                    updateQNo();
                    quickSum();
                }
            });
        }

        if (key === '+') {
            coNum.textContent = parseInt(coNum.textContent) + 1;
            let newDiv = olist.cloneNode(true),
                newRow = coRow.cloneNode(true),
                newItm = qItm.cloneNode(true);
            newDiv.classList.remove('delete-not');
            newDiv.children[0].textContent = 'CO' + coNum.textContent;
            newDiv.children[1].value = '';
            olist.parentNode.appendChild(newDiv);
            newRow.classList.remove('delete-not');
            newRow.children[0].textContent = 'CO' + coNum.textContent;
            newItm.querySelector('.q-item--co').textContent = 'CO' + coNum.textContent;
            qSrc.appendChild(newItm);
            for (let x = 0; x < newRow.querySelectorAll('dd').length; x++) {
                newRow.querySelectorAll('dd')[x].textContent = '';
            }
            let actives = newRow.querySelectorAll('.active');
            for (let x = 0; x < actives.length; x++) {
                actives[x].classList.remove('active');
            }
            coRow.parentNode.appendChild(newRow);
        } else if (key === '-') {
            if (parseInt(coNum.textContent) - 1 > 0) {
                coNum.textContent = parseInt(coNum.textContent) - 1;
                try {olist.parentNode.querySelector('.co-item:not(.delete-not):last-of-type').remove();} catch {}
                try {coRow.parentNode.querySelector('.copo-row:not(.delete-not):last-of-type').remove();} catch {}
                try {qSrc.querySelector('.q-item:last-of-type').remove();} catch {}
            }
        }
    }

