    // Math for each exam
    async function quickSum() {
        let qExam = document.querySelectorAll('#obeBody .assess-alignment .q-match .q-exam');

        for (let x = 0; x < qExam.length; x++) {
            let score = qExam[x].querySelectorAll('.q-item--score'),
                sum = 0;

            for (let y = 0; y < score.length; y++) {
                sum = sum + (parseFloat(score[y].value) || 0);

                score[y].closest('.q-exam').querySelector('.q-sum').textContent = sum;

                let total = parseFloat(score[y].closest('.q-exam').querySelector('.q-total').textContent),
                    bg = score[y].closest('.q-exam').querySelector('.q-score');

                bg.classList.remove('red','green');

                if (sum === total) {
                    bg.classList.add('green');
                } else if (sum > total) {
                    bg.classList.add('red');
                }
            }
        }

        updateQNo();
    }

