    // Disable Non-Number Input for Marks
    function numOnly(t) {
      let value = t.value.replace(/[^0-9\.]/g,'');

      // Only keep the first dot
      value = value.replace(/\./,'dotOne').replace(/\./g,'').replace(/dotOne/,'.');

      // Allow 2 digits max on either side of the dot
      if (value.match(/\./)) {
          let inte = value.replace(/(.*)\..*/,'$1'),
              frac = value.replace(/.*\.(.*)/,'$1');

          if (inte.length > 2) {
              inte = inte.slice(0,2);
          }

          if (frac.length > 2) {
              frac = frac.slice(0,2);
          }

          value = `${inte}.${frac}`;
      } else {
          value = value.slice(0,2);
      }

      // Update input field
      t.value = value;

      // Update sum
      quickSum();
    }

