        lite: ["F2F2F2","FDE9D9","DAEEF3","E4DFEC","EBF1DE","F2DCDB","DCE6F1","F2EAC0","DDD9C4"],
        deep: ["BFBFBF","FABF8F","92CDDC","B1A0C7","C4D79B","DA9694","95B3D7","E6D787","9C925C"]
