const trans4twProps = (text) => {
    if (twProps[text]) {
        return twProps[text]
    }
    const list = text.split(' ')
    if (list.length > 1) {
        return list.map(a => {
            return trans4twProps(a)
        }
                       ).join(' ')
    }else{
        const find = Object.keys(twProps).find(a => text.includes(a))
        if (find) {
            return text.replace(find,twProps[find])
        }
    }
    return list.join(' ')
}

