// ==UserScript==
// @name        canvas allow ^lbutton to open links in new tabs
// @version     1
// @run-at      document-end
// @author      rssaromeo
// @match       *://learn.vccs.edu/*
// @grant       none
// @license     GPLv3
// @icon        data:image/png;base64,
// @description 3/20/2025, 9:55:50 AM
// @namespace https://greasyfork.org/users/1184528
// @downloadURL https://update.greasyfork.org/scripts/530363/canvas%20allow%20%5Elbutton%20to%20open%20links%20in%20new%20tabs.user.js
// @updateURL https://update.greasyfork.org/scripts/530363/canvas%20allow%20%5Elbutton%20to%20open%20links%20in%20new%20tabs.meta.js
// ==/UserScript==

window.addEventListener(
  "click",
  function (e) {
    var elem = e.target
    while (
      elem.parentElement &&
      (elem.tagName !== "A" || !elem.href)
    ) {
      elem = elem.parentElement
    }
    if (elem.tagName !== "A") return
    if (e.ctrlKey && e.button == 0) {
      e.stopImmediatePropagation()
      e.stopPropagation()
      e.preventDefault()
      open(elem.href)
    }
  },
  true
)
