// ==UserScript==
// @name         8chan Beep on Quote (You)
// @namespace    Violentmonkey Scripts
// @match        https://8chan.moe/*/res/*.html*
// @match        https://8chan.se/*/res/*.html*
// @version      0.0.1
// @description  Beeps when someone quotes you (based on <a class="quoteLink you">)
// @author       DS
// @license MIT
// @grant        none
// @downloadURL https://update.greasyfork.org/scripts/533326/8chan%20Beep%20on%20Quote%20%28You%29.user.js
// @updateURL https://update.greasyfork.org/scripts/533326/8chan%20Beep%20on%20Quote%20%28You%29.meta.js
// ==/UserScript==

(function () {
    'use strict';

    // Beep sound (same base64)
    const beep = new Audio('data:audio/wav;base64,');

    const observer = new MutationObserver(mutations => {
        mutations.forEach(mutation => {
            mutation.addedNodes.forEach(node => {
                if (node.nodeType === 1 && node.querySelector('a.quoteLink.you')) {
                    console.log('[Beep] You were quoted.');
                    playBeep();
                }
            });
        });
    });

    observer.observe(document.body, { childList: true, subtree: true });

    function playBeep() {
        if (beep.paused) {
            beep.play().catch(e => console.warn("Beep failed:", e));
        } else {
            beep.addEventListener('ended', () => beep.play(), { once: true });
        }
    }
})();