// ==UserScript==
// @name         Quick Blockchain Explorer Access (@jeetfucker)
// @namespace    http://tampermonkey.net/
// @version      1.9
// @description  Explorer access on Twitter with embedded icons and optimized performance (with true base64 icons)
// @author       You
// @match        https://x.com/*
// @grant        GM_addStyle
// @downloadURL https://update.greasyfork.org/scripts/534427/Quick%20Blockchain%20Explorer%20Access%20%28%40jeetfucker%29.user.js
// @updateURL https://update.greasyfork.org/scripts/534427/Quick%20Blockchain%20Explorer%20Access%20%28%40jeetfucker%29.meta.js
// ==/UserScript==

(function() {
    'use strict';

    const chains = {
        'Solana': {
            icon: 'data:image/png;base64,',
            explorers: {
                'Solscan': 'https://solscan.io',
                'SolanaFM': 'https://solana.fm',
                'Solana Explorer': 'https://explorer.solana.com'
            }
        },
        'Ethereum': {
            icon: 'data:image/png;base64,', // use real base64
            explorers: {
                'Etherscan': 'https://etherscan.io',
                'Blockscout': 'https://blockscout.com/eth/mainnet',
                'Ethplorer': 'https://ethplorer.io'
            }
        },
        'BSC': {
            icon: 'data:image/png;base64,',
            explorers: {
                'BscScan': 'https://bscscan.com',
                'Blockscout': 'https://blockscout.com/bsc/mainnet'
            }
        },
        'Bitcoin': {
            icon: 'data:image/png;base64,',
            explorers: {
                'Blockchain.com': 'https://www.blockchain.com/explorer',
                'Blockstream': 'https://blockstream.info',
                'BTC.com': 'https://btc.com'
            }
        },
        'Base': {
            icon: 'data:image/png;base64,',
            explorers: {
                'Basescan': 'https://basescan.org',
                'Blockscout': 'https://base.blockscout.com'
            }
        }
    };

    const container = document.createElement('div');
    container.id = 'blockchain-container';
    document.body.appendChild(container);

    const widget = document.createElement('div');
    widget.id = 'blockchain-widget';
    container.appendChild(widget);

    const toggleBtn = document.createElement('div');
    toggleBtn.id = 'toggle-button';
    toggleBtn.innerText = 'âš¡';
    container.appendChild(toggleBtn);

    const menu = document.createElement('div');
    menu.id = 'explorer-menu';
    document.body.appendChild(menu);

    let currentButton = null;
    let widgetVisible = true;

    for (const [name, data] of Object.entries(chains)) {
        const iconBtn = document.createElement('img');
        iconBtn.src = data.icon;
        iconBtn.alt = name;
        iconBtn.title = name;
        iconBtn.className = 'chain-icon';
        iconBtn.loading = 'lazy';

        iconBtn.addEventListener('click', (e) => {
            e.stopPropagation();
            currentButton = e.target;
            showExplorerOptions(name);
        });

        widget.appendChild(iconBtn);
    }

    toggleBtn.addEventListener('click', (e) => {
        e.stopPropagation();
        widgetVisible = !widgetVisible;
        widget.style.display = widgetVisible ? 'block' : 'none';
    });

    function showExplorerOptions(chainName) {
        menu.innerHTML = '';
        menu.style.display = 'block';

        const rect = currentButton.getBoundingClientRect();
        menu.style.top = (rect.bottom + window.scrollY + 5) + 'px';
        menu.style.left = (rect.left + window.scrollX) + 'px';

        for (const [explorer, url] of Object.entries(chains[chainName].explorers)) {
            const item = document.createElement('div');
            item.className = 'explorer-item';
            item.innerText = explorer;
            item.onclick = (e) => {
                e.stopPropagation();
                window.open(url, '_blank');
                hideMenu();
            };
            menu.appendChild(item);
        }

        document.addEventListener('click', hideMenu, { once: true });
    }

    function hideMenu() {
        menu.style.display = 'none';
    }

    GM_addStyle(`
        #blockchain-container {
            position: fixed;
            top: 20px;
            right: 20px;
            z-index: 9999;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #blockchain-widget {
            background: white;
            border: 1px solid #ccc;
            padding: 8px;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(0,0,0,0.1);
            margin-bottom: 10px;
        }
        .chain-icon {
            width: 36px;
            height: 36px;
            margin: 5px;
            cursor: pointer;
            border-radius: 50%;
            border: 1px solid #ddd;
            transition: 0.3s;
            object-fit: cover;
        }
        .chain-icon:hover {
            transform: scale(1.2);
        }
        #explorer-menu {
            position: fixed;
            background: white;
            border: 1px solid #ccc;
            box-shadow: 0 0 10px rgba(0,0,0,0.2);
            padding: 5px;
            border-radius: 8px;
            display: none;
            z-index: 10000;
        }
        .explorer-item {
            padding: 5px 10px;
            cursor: pointer;
            white-space: nowrap;
        }
        .explorer-item:hover {
            background: #eee;
        }
        #toggle-button {
            background: #fff;
            border: 1px solid #ccc;
            border-radius: 50%;
            width: 40px;
            height: 40px;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 24px;
            cursor: pointer;
            box-shadow: 0 0 10px rgba(0,0,0,0.1);
        }
        #toggle-button:hover {
            background: #eee;
        }
    `);
})();
