// ==UserScript==
// @name        NoScript - Security & Privacy Info - StartPage Domain Search
// @namespace   https://greasyfork.org/en/users/844240-madmanmoon
// @match       https://noscript.net/about/*
// @grant       none
// @version     1.1
// @author      MadManMoon
// @license     MIT
// @description Adds a StartPage link to the 'Security & Privacy Info' page generated by NoScript from the '?' on a domain - code is annotated and script is currently unpublished on Greasyfork
// @require     https://code.jquery.com/jquery-3.7.1.slim.min.js
// @downloadURL https://update.greasyfork.org/scripts/541054/NoScript%20-%20Security%20%20Privacy%20Info%20-%20StartPage%20Domain%20Search.user.js
// @updateURL https://update.greasyfork.org/scripts/541054/NoScript%20-%20Security%20%20Privacy%20Info%20-%20StartPage%20Domain%20Search.meta.js
// ==/UserScript==

// This part grabs the origin in question:
const Origin = $('main#main-content > h2:first-of-type').text();
// This part builds the StartPage search url (you will get redirected to your local SP):
const StartPageSearch = '<li><a href="https://startpage.com/sp/search?query='+Origin+'&cat=web&pl=opensearch&language=english" target="_blank" rel="nofollow external">Startpage Search</a> about '+Origin+'</li>';
// This part adds the StartPage search as the last list item in the unordered list with the ID of 'resources':
$('ul#resources').append(StartPageSearch);