// ==UserScript==
// @name        NoScript - Add Trustpilot Info To Sec Priv Info Page
// @namespace   https://greasyfork.org/en/users/844240-madmanmoon
// @match       https://noscript.net/about/*
// @grant       none
// @version     1.0
// @author      MadManMoon
// @license     MIT
// @require     https://code.jquery.com/jquery-3.7.1.slim.min.js
// @description Adds a Trustpilot link to the 'Security & Privacy Info' page generated by NoScript from the '?' on a domain - code is annotated and script is currently unpublished on Greasyfork
// @downloadURL https://update.greasyfork.org/scripts/549271/NoScript%20-%20Add%20Trustpilot%20Info%20To%20Sec%20Priv%20Info%20Page.user.js
// @updateURL https://update.greasyfork.org/scripts/549271/NoScript%20-%20Add%20Trustpilot%20Info%20To%20Sec%20Priv%20Info%20Page.meta.js
// ==/UserScript==

//the standard jquery.com is not working for installs - https://code.jquery.com/jquery-3.7.1.slim.min.js
// https://cdn.jsdelivr.net/npm/jquery@3/dist/jquery.min.js

// This part grabs the origin in question:
const Origin = $('main#main-content > h2:first-of-type').text();
// This part builds the StartPage search url (you will get redirected to your local SP):
const TrustpilotInfo = '<li><a href="https://trustpilot.com/review/'+Origin+'" target="_blank" rel="nofollow external">Trustpilot Info</a> about '+Origin+'</li>';
// This part adds the StartPage search as the last list item in the unordered list with the ID of 'resources':
$('ul#resources').append(TrustpilotInfo);