      projectId: "tw2worker",
      storageBucket: "tw2worker.firebasestorage.app",
      messagingSenderId: "582732588183",
      appId: "1:582732588183:web:6efaf0f3427e052b2235d2",
      measurementId: "G-4V2F93Y484"
