define("two/auth", [
  "two/utils",
  "Lockr"
], function (
  utils,
  Lockr
) {
  let isInitialized = false;
  let currentUser = null;
  let authListeners = [];
  let pendingAuthChecks = [];

  const STORAGE_KEYS = {
    USER: "auth_user",
    TOKEN: "auth_token"
  };

  const auth = {
    init: function() {
      if (isInitialized) {
        return false;
      }

      this.loadStoredUser();
      this.interceptWindowManager();
      this.registerGlobalEvents();
      
      isInitialized = true;
      
      return true;
    },

    isInitialized: function() {
      return isInitialized;
    },

    loadStoredUser: function() {
      const userData = Lockr.get(STORAGE_KEYS.USER);
      const token = Lockr.get(STORAGE_KEYS.TOKEN);

      if (userData && token) {
        currentUser = userData;
        this.notifyAuthListeners(currentUser);
      }
    },

    clearStoredUser: function() {
      currentUser = null;
      Lockr.remove(STORAGE_KEYS.USER);
      Lockr.remove(STORAGE_KEYS.TOKEN);
      this.notifyAuthListeners(null);
    },

    saveUser: function(user, token) {
      currentUser = {
        uid: user.uid,
        email: user.email,
        displayName: user.displayName || user.email,
        photoURL: user.photoURL
      };
      
      Lockr.set(STORAGE_KEYS.USER, currentUser);
      Lockr.set(STORAGE_KEYS.TOKEN, token);
      
      this.notifyAuthListeners(currentUser);
    },

    getCurrentUser: function() {
      return currentUser;
    },

    isAuthenticated: function() {
      return currentUser !== null;
    },

    signInWithEmailAndPassword: function(email, password) {
      return new Promise((resolve, reject) => {
        if (!window.firebaseAuth) {
          reject(new Error("Firebase not initialized"));
          return;
        }

        if (!window.signInWithEmailAndPassword) {
          reject(new Error("Firebase signInWithEmailAndPassword not available"));
          return;
        }

        window.signInWithEmailAndPassword(email, password)
          .then(async (result) => {
            const token = await result.user.getIdToken();
            
            this.saveUser(result.user, token);
            
            utils.notif("success", `Welcome back, ${result.user.email}!`);
            resolve(result.user);
          })
          .catch((error) => {
            utils.notif("error", `Login failed: ${error.message}`);
            reject(error);
          });
      });
    },

    createUserWithEmailAndPassword: function(email, password) {
      return new Promise((resolve, reject) => {
        if (!window.firebaseAuth) {
          reject(new Error("Firebase not initialized"));
          return;
        }

        if (!window.createUserWithEmailAndPassword) {
          reject(new Error("Firebase createUserWithEmailAndPassword not available"));
          return;
        }

        window.createUserWithEmailAndPassword(email, password)
          .then(async (result) => {
            const token = await result.user.getIdToken();
            
            this.saveUser(result.user, token);
            
            utils.notif("success", `Account created for ${result.user.email}!`);
            resolve(result.user);
          })
          .catch((error) => {
            utils.notif("error", `Registration failed: ${error.message}`);
            reject(error);
          });
      });
    },

    signOut: function() {
      return new Promise((resolve, reject) => {
        if (!window.firebaseAuth) {
          this.clearStoredUser();
          resolve();
          return;
        }

        if (!window.signOut) {
          this.clearStoredUser();
          resolve();
          return;
        }

        window.signOut()
          .then(() => {
            this.clearStoredUser();
            utils.notif("success", "Signed out successfully");
            resolve();
          })
          .catch((error) => {
            utils.notif("error", `Sign out failed: ${error.message}`);
            reject(error);
          });
      });
    },

    onAuthStateChanged: function(callback) {
      authListeners.push(callback);
      
      callback(currentUser);
      
      return () => {
        const index = authListeners.indexOf(callback);
        if (index > -1) {
          authListeners.splice(index, 1);
        }
      };
    },

    notifyAuthListeners: function(user) {
      authListeners.forEach(callback => {
        try {
          callback(user);
        } catch (error) {
          console.error("Error in auth listener:", error);
        }
      });
    },

    interceptWindowManager: function() {
      if (!windowManagerService || !windowManagerService.getScreenWithInjectedScope) {
        return;
      }

      const originalGetScreen = windowManagerService.getScreenWithInjectedScope;
      const self = this;

      windowManagerService.getScreenWithInjectedScope = function(templateId, scope) {
        const isTWOverflowTemplate = templateId.includes("twoverflow") && templateId.includes("window");
        
        const unprotectedTemplates = [
          "!twoverflow_about_window",
          "!twoverflow_auth_window"
        ];

        const isUnprotected = unprotectedTemplates.some(template => {
          return templateId === template;
        });

        const requiresAuth = isTWOverflowTemplate && !isUnprotected;

        if (requiresAuth && !self.isAuthenticated()) {
          self.requireAuthentication()
            .then(() => {
              originalGetScreen.call(this, templateId, scope);
            })
            .catch(() => {
              utils.notif("error", "Authentication required to access this feature");
            });
          return;
        }

        return originalGetScreen.call(this, templateId, scope);
      };
    },

    requireAuthentication: function() {
      return new Promise((resolve, reject) => {
        if (this.isAuthenticated()) {
          resolve(currentUser);
          return;
        }

        pendingAuthChecks.push({ resolve, reject });

        if (typeof window.authInterface !== "undefined") {
          window.authInterface.openLoginWindow();
        } else {
          reject(new Error("Auth interface not available"));
        }
      });
    },

    resolvePendingChecks: function(user) {
      const checks = [...pendingAuthChecks];
      pendingAuthChecks = [];
      
      checks.forEach(check => {
        check.resolve(user);
      });
    },

    rejectPendingChecks: function(error) {
      const checks = [...pendingAuthChecks];
      pendingAuthChecks = [];
      
      checks.forEach(check => {
        check.reject(error);
      });
    },

    registerGlobalEvents: function() {
      if (typeof eventTypeProvider !== "undefined") {
        if (!eventTypeProvider.AUTH_SHOW_LOGIN) {
          eventTypeProvider.AUTH_SHOW_LOGIN = "auth_show_login";
        }
        if (!eventTypeProvider.AUTH_HIDE_LOGIN) {
          eventTypeProvider.AUTH_HIDE_LOGIN = "auth_hide_login";
        }
        if (!eventTypeProvider.AUTH_STATE_CHANGED) {
          eventTypeProvider.AUTH_STATE_CHANGED = "auth_state_changed";
        }
      }

      this.onAuthStateChanged((user) => {
        if (typeof $rootScope !== "undefined" && $rootScope.$broadcast) {
          $rootScope.$broadcast(eventTypeProvider.AUTH_STATE_CHANGED, user);
        }
      });
    }
  };

  return auth;
});

