      let ratio = 1;
      
      if (totalToSend > maxCapacity) {
        ratio = maxCapacity / totalToSend;
      }
      
      const toSend = {
        wood: Math.min(Math.floor(setting.resources.wood * ratio), resources.wood ? resources.wood.currentStock : 0),
        clay: Math.min(Math.floor(setting.resources.clay * ratio), resources.clay ? resources.clay.currentStock : 0),
        iron: Math.min(Math.floor(setting.resources.iron * ratio), resources.iron ? resources.iron.currentStock : 0)
      };

