define("two/troopCounter", [
  "two/utils",
  "two/debug",
], function (
  utils,
  setupDebug
) {
  let initialized = false;
  const debug = setupDebug("troop_counter");

  const TROOP_TYPES = [
    "spear", "sword", "axe", "archer", "light_cavalry", 
    "mounted_archer", "heavy_cavalry", "ram", "catapult", 
    "doppelsoldner", "trebuchet"
  ];

  const TROOP_NAMES = {
    en_us: [
      "Spear Fighter", "Swordsman", "Axeman", "Archer", "Light Cavalry",
      "Mounted Archer", "Heavy Cavalry", "Ram", "Catapult",
      "Berserker", "Trebuchet"
    ],
    pt_br: [
      "Lanceiro", "Espadachim", "Viking", "Arqueiro", "Cavalaria Leve",
      "Arqueiro Montado", "Cavalaria Pesada", "Aríete", "Catapulta",
      "Berserker", "Trabuco"
    ]
  };

  const PROPERTY_TYPES = ["own", "recruiting", "available"];

  // Format number with dots for thousands
  const formatNumber = function(number) {
    const str = String(number);
    const length = str.length;
    
    if (length < 4) {
      return str.padStart(11);
    } else {
      let formatted = str.padStart(11);
      let chars = formatted.split('');
      
      if (length < 7) {
        // Thousands
        chars.splice(8, 0, ".");
        return chars.join('').substr(1);
      } else if (length < 10) {
        // Millions
        chars.splice(8, 0, ".");
        chars.splice(5, 0, ".");
        return chars.join('').substr(2);
      } else {
        // Billions or more
        return chars.join('');
      }
    }
  };

  const countUnits = function() {
    const villages = modelDataService.getVillages();
    const troopsCount = {
      total: [],
      missing: []
    };

    // Initialize arrays
    for (let i = 0; i < TROOP_TYPES.length; i++) {
      troopsCount.total[i] = 0;
      troopsCount.missing[i] = 0;
    }

    // Count troops by property type
    for (const property of PROPERTY_TYPES) {
      const troops = new Array(TROOP_TYPES.length).fill(0);

      for (const key in villages) {
        if (villages.hasOwnProperty(key)) {
          const village = villages[key];
          
          for (let i = 0; i < TROOP_TYPES.length; i++) {
            const troopType = TROOP_TYPES[i];
            if (village.unitInfo && village.unitInfo.units && village.unitInfo.units[troopType]) {
              troops[i] += village.unitInfo.units[troopType][property] || 0;
            }
          }
        }
      }

      troopsCount[property] = troops.slice();
    }

    // Calculate totals and missing
    for (let i = 0; i < TROOP_TYPES.length; i++) {
      troopsCount.total[i] = troopsCount.own[i] + troopsCount.recruiting[i];
      troopsCount.missing[i] = troopsCount.own[i] - troopsCount.available[i];
    }

    debug(1, "troops count result: %o", troopsCount);
    return troopsCount;
  };

  const formatTroopsData = function(troopsCount) {
    const locale = $rootScope.loc.ale;
    const troopNames = TROOP_NAMES[locale] || TROOP_NAMES.en_us;
    
    const formattedTroops = [];
    
    for (let i = 0; i < TROOP_TYPES.length; i++) {
      formattedTroops.push({
        type: TROOP_TYPES[i],
        name: troopNames[i],
        own: formatNumber(troopsCount.own[i]),
        recruiting: formatNumber(troopsCount.recruiting[i]),
        total: formatNumber(troopsCount.total[i]),
        available: formatNumber(troopsCount.available[i]),
        missing: formatNumber(troopsCount.missing[i]),
        // Valores numéricos para ordenação
        ownNum: troopsCount.own[i],
        recruitingNum: troopsCount.recruiting[i],
        totalNum: troopsCount.total[i],
        availableNum: troopsCount.available[i],
        missingNum: troopsCount.missing[i]
      });
    }
    
    return formattedTroops;
  };

  const troopCounter = {};

  troopCounter.init = function() {
    debug(1, "initialized");
    initialized = true;
  };

  troopCounter.countAndDisplay = function() {
    debug(1, "counting and displaying troops");
    
    const troopsCount = countUnits();
    const formattedTroops = formatTroopsData(troopsCount);
    
    return {
      rawData: troopsCount,
      formattedTroops: formattedTroops
    };
  };

  troopCounter.isInitialized = function() {
    return initialized;
  };

  return troopCounter;
});

