// ==UserScript==
// @name         Chaturbate PM notifier +
// @namespace    http://tampermonkey.net/
// @version      0.2
// @description  Blink title and play sound when you receive a PM. OBS, works only by detecting if you have unread PM's so will not work when you are in a chat.
// @author       Brsrk
// @license      MIT
// @icon         https://www.google.com/s2/favicons?sz=32&domain=chaturbate.com
// @icon64       https://www.google.com/s2/favicons?sz=64&domain=chaturbate.com
// @match        https://chaturbate.com/*
// @grant        none
// @run-at       document-end
// @downloadURL https://update.greasyfork.org/scripts/551044/Chaturbate%20PM%20notifier%20%2B.user.js
// @updateURL https://update.greasyfork.org/scripts/551044/Chaturbate%20PM%20notifier%20%2B.meta.js
// ==/UserScript==

(function() {
    'use strict';

    const pmElementSelector = '#pm-tab-default';
    const monitorText = {
        after: 'PM \\(\\d+\\)',
    };

  // Define the sound to play
  const sound = new Audio('data:audio/mpeg;base64,'); // Base64 encoded audio
    sound.volume = 1;

    let previousTextContent = '';
    let titleBlinkInterval = null;
    let currentTitle = document.title;

    function checkForChanges() {
        const pmElement = document.querySelector(pmElementSelector);
        if (!pmElement) return;

        const textContent = pmElement.textContent;
        if (textContent === previousTextContent) return;

        previousTextContent = textContent;

        const match = new RegExp(monitorText.after).test(textContent);
        if (match) {
            blinkTitle('游린游린游린游린游린游린游린游린游린游린游린游린游린');
            sound.play();
        } else {
            stopBlinkingTitle();
            document.title = currentTitle;
        }
    }

    function blinkTitle(prefix) {
        stopBlinkingTitle();
        let visible = true;
        titleBlinkInterval = setInterval(() => {
            document.title = visible ? currentTitle : prefix + currentTitle;
            visible = !visible;
        }, 1500);
    }

    function stopBlinkingTitle() {
        if (titleBlinkInterval) {
            clearInterval(titleBlinkInterval);
            titleBlinkInterval = null;
        }
    }

    const titleObserver = new MutationObserver(() => {
        currentTitle = document.title;
    });
    titleObserver.observe(document.querySelector('head > title'), {
        childList: true,
        characterData: true,
    });

    function observeChanges() {
        const pmElement = document.querySelector(pmElementSelector);
        if (!pmElement) {
            requestAnimationFrame(observeChanges);
            return;
        }

        const observer = new MutationObserver(checkForChanges);
        observer.observe(pmElement, {
            childList: true,
            subtree: true,
            characterData: true,
        });
    }

    observeChanges();
})();