// ==UserScript==
// @name         Nyaa âžœ AnimeTosho Search Button
// @version      1.0
// @description  Adds an Anime Tosho search button to nyaa torrent pages for anime.
// @author       AnimeIsMyWaifu
// @match        https://nyaa.si/view/*
// @icon         data:image/png;base64,
// @grant        none
// @namespace https://greasyfork.org/users/1225995
// @downloadURL https://update.greasyfork.org/scripts/552713/Nyaa%20%E2%9E%9C%20AnimeTosho%20Search%20Button.user.js
// @updateURL https://update.greasyfork.org/scripts/552713/Nyaa%20%E2%9E%9C%20AnimeTosho%20Search%20Button.meta.js
// ==/UserScript==

(function () {
    'use strict';

    window.addEventListener('load', () => {
        const titleEl = document.querySelector('h3.panel-title');
        if (!titleEl) return;

        const torrentTitle = titleEl.textContent.trim();
        if (!torrentTitle) return;

        const rows = document.querySelectorAll('.panel-body .row');
        let isAnimeEnglish = false;

        for (const row of rows) {
            const label = row.querySelector('.col-md-1');
            if (label && label.textContent.trim() === 'Category:') {
                const categoryLinks = row.querySelectorAll('.col-md-5 a');
                if (categoryLinks.length >= 2) {
                    const mainCat = categoryLinks[0].textContent.trim();
                    const subCat = categoryLinks[1].textContent.trim();
                    if (mainCat === 'Anime' && subCat === 'English-translated') {
                        isAnimeEnglish = true;
                    }
                }
                break;
            }
        }

        if (!isAnimeEnglish) return;

        const btn = document.createElement('a');
        btn.textContent = 'ðŸ” Search on Anime Tosho';
        btn.href = `https://animetosho.org/search?q=${encodeURIComponent(torrentTitle)}`;
        btn.target = '_blank';
        btn.style.background = '#4C5A85';
        btn.style.color = 'white';
        btn.style.padding = '2px 7px';
        btn.style.borderRadius = '6px';
        btn.style.fontWeight = 'bold';
        btn.style.textDecoration = 'none';
        btn.style.fontSize = '10px';
        btn.style.transition = 'background 0.2s';
        btn.addEventListener('mouseover', () => (btn.style.background = '#3C4768'));
        btn.addEventListener('mouseout', () => (btn.style.background = '#4C5A85'));

        let infoHashRow = null;
        for (const row of rows) {
            const label = row.querySelector('.col-md-1');
            if (label && label.textContent.trim() === 'Info hash:') {
                infoHashRow = row;
                break;
            }
        }

        if (!infoHashRow) return;

        const newRow = document.createElement('div');
        newRow.className = 'row';
        newRow.innerHTML = `
            <div class="col-md-offset-6 col-md-1" style="white-space: nowrap;">Anime Tosho:</div>
            <div class="col-md-5"></div>
        `;

        const valueCell = newRow.querySelector('.col-md-5');
        valueCell.appendChild(btn);

        infoHashRow.insertAdjacentElement('afterend', newRow);
    });
})();
