    // ========== Â∑•ÂÖ∑ÂáΩÊï∞ ==========
    function log(level, message, force = false) {
        if (force || logLevels[level] >= currentLogLevel) {
            const timestamp = new Date().toISOString().slice(11, 23);
            const styledMessage = `%c[${timestamp}] [${level}] ${message}`;
            const styles = {
                'DEBUG': 'color: #BB86FC;',
                'INFO': 'color: #03DAC6; font-weight: bold;',
                'WARN': 'color: #FFD60A; font-weight: bold;',
                'ERROR': 'color: #CF6679; font-weight: bold;'
            };
            console.log(styledMessage, styles[level]);
        }
    }

    // Ëé∑ÂèñÊ†ºÂºèÂåñÊó∂Èó¥Êà≥ (Êó∂:ÂàÜ:Áßí)
    function getFormattedTimestamp() {
        const now = new Date();
        const hours = String(now.getHours()).padStart(2, '0');
        const minutes = String(now.getMinutes()).padStart(2, '0');
        const seconds = String(now.getSeconds()).padStart(2, '0');
        return `${hours}:${minutes}:${seconds}`;
    }

    // ‰∏âË°åÊªöÂä®Êó•ÂøóÂáΩÊï∞
    function showScrollingLog(message, level = 'INFO') {
        const statusDiv = document.getElementById('jm-status');
        if (!statusDiv) return;

        const timestamp = getFormattedTimestamp();
        const logEntry = `[${timestamp}] ${message}`;
        const logColor = {
            'DEBUG': '#BB86FC',
            'INFO': '#03DAC6',
            'WARN': '#FFD60A',
            'ERROR': '#CF6679'
        }[level] || '#03DAC6';

        // Ëé∑ÂèñÁé∞ÊúâÁöÑÊó•ÂøóÊï∞ÁªÑÔºåÂ¶ÇÊûúÊ≤°ÊúâÂàôÂàùÂßãÂåñ
        let logs = statusDiv.dataset.logs ? JSON.parse(statusDiv.dataset.logs) : [];
        // Ê∑ªÂä†Êñ∞Êó•ÂøóÂà∞Êï∞ÁªÑÂºÄÂ§¥
        logs.unshift({ text: logEntry, color: logColor });

        // ÈôêÂà∂Êï∞ÁªÑÈïøÂ∫¶‰∏∫3
        if (logs.length > 3) {
            const removedLog = logs.pop(); // ÁßªÈô§ÊúÄÊóßÁöÑÊó•Âøó
            // ‰∏∫Ë¢´ÁßªÈô§ÁöÑÊó•ÂøóËÆæÁΩÆÊ∑°Âá∫ÊïàÊûúÂíåÂª∂ËøüÁßªÈô§
            setTimeout(() => {
                // Ê£ÄÊü•ÂΩìÂâçÊòæÁ§∫ÁöÑÊó•ÂøóÊòØÂê¶ÂåÖÂê´Ë¢´ÁßªÈô§ÁöÑÊó•ÂøóÂÜÖÂÆπ
                const currentText = statusDiv.innerHTML;
                if (currentText.includes(removedLog.text)) {
                    // Â¶ÇÊûúÂΩìÂâçÊòæÁ§∫ÁöÑÂåÖÂê´‰∫ÜË¶ÅÁßªÈô§ÁöÑÔºåËØ¥ÊòéÂÆÉÂ∑≤ÁªèË¢´Êñ∞ÁöÑË¶ÜÁõñ‰∫ÜÔºåÊó†ÈúÄÊìç‰Ωú
                    // Â¶ÇÊûúÂÆÉËøòÂú®ÊòæÁ§∫ÔºåÊàë‰ª¨ÈúÄË¶ÅÊõøÊç¢ÂÆÉ
                    const lines = currentText.split('<br>');
                    const updatedLines = lines.map(line => {
                        if (line.includes(removedLog.text)) {
                            return line.replace(/opacity:\s*1;/, 'opacity: 0.3; transition: opacity 0.5s ease;')
                                .replace(/color:\s*[^;]+;/, `color: ${removedLog.color};`);
                        }
                        return line;
                    });
                    statusDiv.innerHTML = updatedLines.join('<br>');
                    // 0.5ÁßíÂêéÂÆåÂÖ®ÁßªÈô§
                    setTimeout(() => {
                        const finalLines = updatedLines.filter(line => !line.includes(removedLog.text));
                        statusDiv.innerHTML = finalLines.join('<br>');
                    }, 500);
                }
            }, 2000); // 2ÁßíÂêéÂºÄÂßãÊ∑°Âá∫
        }

        // Êõ¥Êñ∞ data Â±ûÊÄß
        statusDiv.dataset.logs = JSON.stringify(logs);

        // ÈáçÊûÑHTMLÂÜÖÂÆπ
        const htmlLines = logs.map((log, index) => {
            // ÊúÄÊñ∞ÁöÑ‰∏ÄË°å (index === 0) ÊåÅÁª≠ÊòæÁ§∫Ôºå‰∏çËÆæÊ∑°Âá∫
            const opacityStyle = index === 0 ? 'opacity: 1;' : 'opacity: 1; transition: opacity 0.5s ease;';
            return `<span style="color: ${log.color}; ${opacityStyle}">${log.text}</span>`;
        });

        statusDiv.innerHTML = htmlLines.join('<br>');
    }


    function isVisible(el) {
        if (!el || !(el instanceof HTMLElement)) return false;
        const style = window.getComputedStyle(el);
        if (
            style.display === 'none' ||
            style.visibility === 'hidden' ||
            parseFloat(style.opacity) <= 0 ||
            el.offsetWidth === 0 ||
            el.offsetHeight === 0
        ) {
            return false;
        }
        const rect = el.getBoundingClientRect();
        return (
            rect.bottom > 0 &&
            rect.top < (window.innerHeight || document.documentElement.clientHeight) &&
            rect.right > 0 &&
            rect.left < (window.innerWidth || document.documentElement.clientWidth)
        );
    }

    function safeClickElement(el) {
        if (!el) return false;
        if (!isVisible(el)) {
            el.scrollIntoView({ behavior: 'auto', block: 'center' });
            // Á≠âÂæÖÊªöÂä®ÂÆåÊàê
            return new Promise(resolve => setTimeout(() => {
                if (isVisible(el)) {
                    const event = new MouseEvent('click', {
                        view: window,
                        bubbles: true,
                        cancelable: true,
                        button: 0
                    });
                    el.dispatchEvent(event);
                    resolve(true);
                } else {
                    resolve(false);
                }
            }, 300));
        } else {
            const event = new MouseEvent('click', {
                view: window,
                bubbles: true,
                cancelable: true,
                button: 0
            });
            el.dispatchEvent(event);
            return Promise.resolve(true);
        }
    }

    function safeClickRadio(input) {
        if (!input || !(input instanceof HTMLInputElement)) return false;
        if (input.checked) return true;

        input.scrollIntoView({ behavior: 'auto', block: 'center' });

        const container = input.closest('.el-radio') || input.parentElement;
        if (container && !isVisible(container)) {
            container.scrollIntoView({ behavior: 'auto', block: 'center' });
        }

        input.checked = true;
        input.dispatchEvent(new Event('change', { bubbles: true }));
        input.dispatchEvent(new Event('input', { bubbles: true }));

        if (container && container.offsetParent !== null) {
            setTimeout(() => {
                if (isVisible(container)) {
                    const event = new MouseEvent('click', {
                        view: window,
                        bubbles: true,
                        cancelable: true,
                        button: 0
                    });
                    container.dispatchEvent(event);
                }
            }, 50);
        }

        return input.checked;
    }

