    // ========== ËßÜÈ¢ëÊ®°ÂùóÁõ∏ÂÖ≥ÂáΩÊï∞ ==========
    function enforceSilence(video) {
        if (video.hasAttribute('data-jm-auto-disabled')) return;
        video.muted = true;
        video.volume = 0;
    }

    function handleVideos() {
        if (!config.autoMute) return;
        document.querySelectorAll('video').forEach(video => {
            if (video.hasAttribute('data-jm-listening')) return;
            video.setAttribute('data-jm-listening', 'true');
            enforceSilence(video);

            video.addEventListener('play', () => {
                video.removeAttribute('data-jm-auto-disabled');
                log('DEBUG', 'ËßÜÈ¢ëÊí≠ÊîæÔºåÂ∑≤ÊÅ¢Â§çÈùôÈü≥');
            });
        });
    }

    function startSilenceGuard() {
        if (silenceGuardActive || !config.autoMute) return;
        silenceGuardActive = true;
        silenceGuardInterval = setInterval(() => {
            document.querySelectorAll('video').forEach(enforceSilence);
        }, 800);
        window.addEventListener('beforeunload', () => clearInterval(silenceGuardInterval));
    }

    function stopSilenceGuard() {
        if (silenceGuardInterval) {
            clearInterval(silenceGuardInterval);
            silenceGuardInterval = null;
            silenceGuardActive = false;
            log('INFO', 'ÈùôÈü≥ÂÆàÊä§Â∑≤ÂÅúÊ≠¢');
            showScrollingLog('ÈùôÈü≥ÂÆàÊä§Â∑≤ÂÅúÊ≠¢', 'INFO');
        }
    }

    function autoJumpToUnfinishedVideo() {
        if (!config.autoJumpUnfinished) return;
        const items = document.querySelectorAll('.directoryList .item');
        let foundUnfinished = false;
        for (const item of items) {
            const statusEl = item.querySelector('.status');
            if (!statusEl) continue;
            const text = statusEl.textContent.trim();
            const match = text.match(/^Â∑≤Â≠¶(\d+)%$/);
            if (match && parseInt(match[1], 10) < 100) {
                safeClickElement(item);
                log('INFO', `Ëá™Âä®Ë∑≥ËΩ¨Ëá≥Êú™ÂÆåÊàêËßÜÈ¢ëÔºö${item.querySelector('.title')?.textContent}`);
                showScrollingLog(`Ëá™Âä®Ë∑≥ËΩ¨Ëá≥Êú™ÂÆåÊàêËßÜÈ¢ë`, 'INFO');
                foundUnfinished = true;
                break;
            }
        }
        if (!foundUnfinished) {
            log('DEBUG', 'Êú™ÊâæÂà∞Êú™ÂÆåÊàêËßÜÈ¢ë');
        }
    }

    function trySign() {
        if (!config.autoSign) return false;
        const now = Date.now();
        const MIN_INTERVAL = 5000;
        const spans = document.querySelectorAll('button.el-button--primary span');
        for (const span of spans) {
            if (span.textContent.trim() !== 'ÁÇπÂáªÁ≠æÂà∞') continue;
            const button = span.closest('button');
            if (!button || button.disabled || button.classList.contains('is-disabled') || !isVisible(button)) continue;
            log('INFO', 'Á≠æÂà∞ÊåâÈíÆÂ∑≤ÁÇπÂáª');
            showScrollingLog('Á≠æÂà∞ÊåâÈíÆÂ∑≤ÁÇπÂáª', 'INFO');
            safeClickElement(button);
            return true;
        }
        return false;
    }

